import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './cart.css';
import axios from 'axios';
import { useAppContext } from '../../context/AppContext';

export const Cart = () => {
  const { user } = useAppContext();
  const [cartItems, setCartItems] = useState([]);
  const [selectedItems, setSelectedItems] = useState([]);
  const [totalSelectedPrice, setTotalSelectedPrice] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchCart = async () => {
      if (user && user.id) {
        setLoading(true);
        setError(null);
        try {
          const response = await axios.get(`http://localhost:3003/api/cart/user/${user.id}`);
          setCartItems(response.data);
        } catch (err) {
          console.error('Lỗi khi lấy giỏ hàng:', err);
          setError('Không thể tải giỏ hàng. Vui lòng thử lại sau.');
        } finally {
          setLoading(false);
        }
      } else {
        setCartItems([]); // Nếu không có user id, giỏ hàng rỗng
      }
    };

    fetchCart();
  }, [user]);

  useEffect(() => {
    // Tính tổng tiền cho các sản phẩm được chọn
    const total = cartItems
      .filter(item => selectedItems.includes(item._id))
      .reduce((sum, item) => sum + item.product.price * item.quantity, 0);
    setTotalSelectedPrice(total);
  }, [selectedItems, cartItems]);

  const handleSelectAll = () => {
    if (selectedItems.length === cartItems.length) {
      setSelectedItems([]);
    } else {
      setSelectedItems(cartItems.map(item => item._id));
    }
  };

  const handleSelectItem = (productId) => {
    setSelectedItems(prevState =>
      prevState.includes(productId)
        ? prevState.filter(id => id !== productId)
        : [...prevState, productId]
    );
  };

  const handleQuantityChange = async (productId, newQuantity) => {
    if (!user || !user.id) {
      alert('Bạn cần đăng nhập để thay đổi giỏ hàng.');
      return;
    }

    const itemToUpdate = cartItems.find(item => item._id === productId);
    if (!itemToUpdate) return;

    if (newQuantity < 1 || newQuantity > itemToUpdate.product.quantity) {
      return; // Không cho số lượng bé hơn 1 hoặc lớn hơn số lượng kho
    }

    try {
      await axios.put(`http://localhost:3003/api/cart/update/${productId}`, {
        quantity: newQuantity,
      });
      // Cập nhật local state
      setCartItems(prevItems =>
        prevItems.map(item =>
          item._id === productId ? { ...item, quantity: newQuantity } : item
        )
      );
    } catch (error) {
      console.error('Lỗi cập nhật giỏ hàng:', error);
      alert('Có lỗi xảy ra khi cập nhật giỏ hàng.');
    }
  };

  const handleDecrease = (productId) => {
    const currentItem = cartItems.find(item => item._id === productId);
    if (currentItem && currentItem.quantity > 1) {
      handleQuantityChange(productId, currentItem.quantity - 1);
    }
  };

  const handleIncrease = (productId) => {
    const currentItem = cartItems.find(item => item._id === productId);
    if (currentItem && currentItem.quantity < currentItem.product.quantity) {
      handleQuantityChange(productId, currentItem.quantity + 1);
    }
  };

  const handleDelete = async (productId) => {
    if (!user || !user.id) {
      alert('Bạn cần đăng nhập để xóa sản phẩm khỏi giỏ hàng.');
      return;
    }

    try {
      await axios.delete(`http://localhost:3003/api/cart/delete/${productId}`);
      // Cập nhật local state
      setCartItems(prevItems => prevItems.filter(item => item._id !== productId));
      setSelectedItems(prevSelected => prevSelected.filter(id => id !== productId));
    } catch (error) {
      console.error('Lỗi xóa sản phẩm khỏi giỏ hàng:', error);
      alert('Có lỗi xảy ra khi xóa sản phẩm.');
    }
  };

  const handleBuyNow = () => {
    if (selectedItems.length === 0) {
      alert('Vui lòng chọn sản phẩm bạn muốn mua.');
      return;
    }
    // Chuyển đến trang thanh toán với các sản phẩm đã chọn
    // Có thể sử dụng navigate và truyền state hoặc params
  };

  if (loading) return <div className="cart"><p>Đang tải giỏ hàng...</p></div>;
  if (error) return <div className="cart"><p className="error">{error}</p></div>;
  if (!user) return <div className="cart"><p>Vui lòng <Link to="/login">đăng nhập</Link> để xem giỏ hàng.</p></div>;
  if (cartItems.length === 0) {
    return (
      <div className="cart">
        <h2>Giỏ hàng của bạn</h2>
        <div className="empty-cart">
          <p>Giỏ hàng trống</p>
          <Link to="/" className="btn-continue">Tiếp tục mua sắm</Link>
        </div>
      </div>
    );
  }

  return (
    <div className="cart">
      <h2>Giỏ hàng của bạn</h2>
      <div className="cart-header">
        <input
          type="checkbox"
          checked={selectedItems.length === cartItems.length}
          onChange={handleSelectAll}
        />
        <span>Chọn tất cả ({selectedItems.length})</span>
      </div>

      <div className="cart-items">
        {cartItems.map((item) => (
          <div className="cart-item" key={item._id}>
            <input
              type="checkbox"
              checked={selectedItems.includes(item._id)}
              onChange={() => handleSelectItem(item._id)}
            />
            <img src={item.product.image[0]} alt={item.product.name} />
            <div className="cart-item-info">
              <h4>{item.product.name}</h4>
              <p>Giá: đ{item.product.price?.toLocaleString()}</p>

              <div className="quantity-control">
                <button onClick={() => handleDecrease(item._id)}>-</button>
                <input type="number" value={item.quantity} min="1" max={item.product.quantity} readOnly />
                <button onClick={() => handleIncrease(item._id)}>+</button>
              </div>

              <p>Thành tiền: đ{(item.product.price * item.quantity)?.toLocaleString()}</p>

              <button className="btn-delete" onClick={() => handleDelete(item._id)}>
                Xóa
              </button>
            </div>
          </div>
        ))}
      </div>

      <div className="cart-footer">
        <div className="footer-left">
          <input
            type="checkbox"
            checked={selectedItems.length === cartItems.length}
            onChange={handleSelectAll}
          />
          <span>Chọn tất cả ({selectedItems.length})</span>
        </div>

        <div className="footer-right">
          <p>Tổng thanh toán: <span>đ{totalSelectedPrice?.toLocaleString()}</span></p>
          <button
            className="btn-buy"
            disabled={selectedItems.length === 0}
            onClick={handleBuyNow}
          >
            Mua hàng
          </button>
        </div>
      </div>
    </div>
  );
};
