import React, { useState } from 'react';
import './customerprofile.css';
import axios from 'axios';

export const CustomerProfile = () => {
    const [formData, setFormData] = useState({
        fullName: '',
        phone: '',
        address: '',
        birthday: '',
        gender: '',
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            // Assuming your backend route for creating customers is '/api/customers'
            const response = await axios.post('http://localhost:3001/api/customers', formData);
            console.log('Customer saved successfully:', response.data);
            // Optionally, you can reset the form or show a success message to the user
            setFormData({
                fullName: '',
                phone: '',
                address: '',
                birthday: '',
                gender: '',
            });
            alert('Thông tin khách hàng đã được lưu!');
        } catch (error) {
            console.error('Error saving customer:', error.message);
            // Optionally, you can show an error message to the user
            alert('Đã có lỗi xảy ra khi lưu thông tin khách hàng.');
        }
    };

    return (
        <div className="customer-profile">
            <h2>Thông Tin Khách Hàng</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Họ và tên</label>
                    <input
                        type="text"
                        name="fullName"
                        value={formData.fullName}
                        onChange={handleChange}
                        required
                        className="input-phone"
                    />
                </div>
                <div className="form-group">
                    <label>Điện Thoại</label>
                    <input
                        type="text"
                        name="phone"
                        value={formData.phone}
                        onChange={handleChange}
                        required
                        className="input-phone"
                    />
                </div>
                <div className="form-group">
                    <label>Địa chỉ</label>
                    <input
                        type="text"
                        name="address"
                        value={formData.address}
                        onChange={handleChange}
                        required
                        className="input-phone"
                    />
                </div>

                <div className="form-group">
                    <label>Sinh Nhật</label>
                    <input
                        type="date"
                        name="birthday"
                        value={formData.birthday}
                        onChange={handleChange}
                        required
                        className="input-date"
                    />
                </div>

                <div className="form-group">
                    <label>Giới tính</label>
                    <select
                        name="gender"
                        value={formData.gender}
                        onChange={handleChange}
                        required
                        className="input-gender"
                    >
                        <option value="">Chọn giới tính</option>
                        <option value="male">Nam</option>
                        <option value="female">Nữ</option>
                        <option value="other">Khác</option>
                    </select>
                </div>
                <button type="submit" className='updateProfile'>
                    Lưu
                </button>
            </form>
        </div>
    );
};
