import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './shoppages.css';
import { useParams } from 'react-router-dom';

export const ShopPages = () => {
    const { sellerId } = useParams();
    const [products, setProducts] = useState([]);
    const [shopInfo, setShopInfo] = useState({
        name: "Ten Shope",
        avatar: "",
        lastOnline: "7 minutes ago",
    });

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const res = await axios.get(`http://localhost:4003/api/product/sellers/${sellerId}`);
                console.log("Dữ liệu trả về từ API:", res.data); // Kiểm tra dữ liệu trả về từ API
                setProducts(res.data);
            } catch (error) {
                console.error('Lỗi khi lấy sản phẩm:', error);
            }
        };

        fetchProducts();
    }, [sellerId]);

    if (products.length === 0) {
        return <div>Loading products...</div>;
    }

    return (
        <div className="shop-page">
            <div className='shopIno'>
                <div className="shop-info-left">
                    <img
                        className="shop-avatar"
                        src={shopInfo.avatar || "/default-avatar.png"} // Đảm bảo có ảnh đại diện
                        alt="Shop Avatar"
                    />
                    <div className="shop-details">
                        <h2 className="shop-name">{shopInfo.name}</h2>
                        <p className="shop-online">Online: {shopInfo.lastOnline}</p>
                    </div>
                </div>
            </div>
            <div className="shop__category">
                <ul>
                    <li>Danh mục 1</li>
                    <li>Danh mục 2</li>
                    <li>Danh mục 3</li>
                </ul>
            </div>
            {/* <div className="shop-products">
                {products.map((product) => (
                    <div key={product._id} className="product-item">
                        <img
                            src={product.images || "/default-image.png"} 
                            alt={product.name}
                            className="product-image"
                        />
                        <div className="product-info">
                            <h3 className="product-name">{product.name}</h3>
                            <p className="product-category">{product.categoryId.name}</p>
                            <p className="product-price">${product.price}</p>
                        </div>
                    </div>
                ))}
            </div> */}
        </div>
    );
};
