import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './orderAdmin.css';
import { useAppContext } from '../../context/AppContext';

export const OrderAdmin = () => {
  const [orders, setOrders] = useState([]);
  const [orderDetails, setOrderDetails] = useState([]);
  const [loading, setLoading] = useState(true);
  const { token } = useAppContext();

  const fetchData = async () => {
    try {
      const [ordersRes, orderDetailsRes] = await Promise.all([
        axios.get('http://localhost:4000/api/order/all', {
          headers: { Authorization: `Bearer ${token}` }
        }),
        axios.get('http://localhost:4001/api/orderDetails/all', {
          headers: { Authorization: `Bearer ${token}` }
        })
      ]);

      // Gắn các orderDetails tương ứng vào từng order
      const ordersWithDetails = ordersRes.data.map(order => {
        const details = orderDetailsRes.data.filter(
          detail => detail.orderId === order._id
        );
        return { ...order, orderDetails: details };
      });

      setOrders(ordersWithDetails);
      setLoading(false);
    } catch (error) {
      console.error('Lỗi khi lấy dữ liệu:', error);
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  if (loading) return <p>Đang tải đơn hàng...</p>;

  return (
    <div className="order-admin">
      <h2>Danh sách tất cả đơn hàng</h2>
      <table>
        <thead>
          <tr>
            <th>Mã đơn</th>
            <th>Khách hàng</th>
            <th>Tổng tiền</th>
            <th>Trạng thái</th>
            <th>Phương thức thanh toán</th>
            <th>Vận chuyển</th>
            <th>Ngày tạo</th>
            <th>Chi tiết</th>
          </tr>
        </thead>
        <tbody>
          {orders.map(order => (
            <tr key={order._id}>
              <td>{order._id}</td>
              <td>{order.userId?.name || 'Không có'}</td>
              <td>{order.totalOrder.toLocaleString()} VND</td>
              <td>{order.status}</td>
              <td>{order.paymentId?.paymentMethod || 'Không có'}</td>
              <td>{order.transportId?.shippingCarrier || 'Không có'}</td>
              <td>{new Date(order.createdAt).toLocaleString()}</td>
              <td>
                {order.orderDetails.length > 0 ? (
                  order.orderDetails.map(detail => (
                    <div key={detail._id} className="order-detail">
                      <p>{detail.productId?.name || 'Sản phẩm không xác định'}</p>
                      <img src={detail.productId?.image} width={50} alt="" />
                      <p>Số lượng: {detail.quantity}</p>
                    </div>
                  ))
                ) : (
                  <p>Không có</p>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};
