import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './sellershop.css';

export const SellerShop = ({ productId }) => {
  const [shopInfo, setShopInfo] = useState(null);

  useEffect(() => {
    const fetchSellerInfo = async () => {
      try {
        const response = await axios.get(`http://localhost:4003/api/product/seller/${productId}`);
        setShopInfo(response.data);
      } catch (error) {
        console.error('Error fetching shop info:', error);
      }
    };

    if (productId) {
      console.log("Fetching seller info for productId:", productId);
      fetchSellerInfo();
    }
  }, [productId]);

  if (!shopInfo) return <div>Loading shop info...</div>;

  return (
    <div className="shop-header">
      <div className="shop-info-left">
        <img
          className="shop-avatar"
          src={shopInfo.image || 'default-avatar.png'}
          alt="Shop Avatar"
        />
        <div className="shop-details">
          <h2 className="shop-name">{shopInfo.storeName}</h2>
          <p className="shop-online">Online: 7 minutes ago</p>
          <div className="shop-buttons">
            <button className="view-btn">üè™ Xem Shop</button>
          </div>
        </div>
      </div>

      <div className="shop-info-right">
        <div className="shop-stat">
          <p className="stat-title">ƒê·ªãa ch·ªâ: {shopInfo.storeAddress}</p>
          <p className="stat-title">S·ªë ƒëi·ªán tho·∫°i: {shopInfo.phone}</p>
        </div>
      </div>
    </div>
  );
};
