import React, { useState } from 'react';
import { useAppContext } from '../../context/AppContext';
import axios from 'axios';
import './order.css';

export const Order = () => {
  const { cartItems, products, customerId, cartId, token } = useAppContext();
  const [paymentMethod, setPaymentMethod] = useState('cod');
  const [shippingInfo, setShippingInfo] = useState({
    name: '',
    phone: '',
    province: '',
    address: ''
  });

  const selectedItems = cartItems.filter(item => item.selected);
  const getProductById = (id) => products.find(p => p._id === id);

  const totalPrice = selectedItems.reduce((acc, item) => {
    const prod = getProductById(item.productId);
    if (!prod) return acc;
    const price = prod.discounted_price || prod.price || 0;
    return acc + price * item.quantity;
  }, 0);

  const handleOrderSubmit = async () => {
    try {
      if (!customerId || !token) {
        alert("Thiếu thông tin đăng nhập");
        return;
      }

      const paymentRes = await axios.post(
        'http://localhost:3007/api/payment/create',
        {
          paymentMethod,
          paymentStatus: 'Pending'
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      const paymentId = paymentRes.data.payment?._id;
      if (!paymentId) throw new Error('Không lấy được paymentId');

      const orderRes = await axios.post(
        'http://localhost:4000/api/order/create',
        {
          totalOrder: totalPrice,
          discountId: [],
          cartId,
          customerId,
          paymentId,
          status: 'pending',
          shippingInfo
        },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      const orderId = orderRes.data.order?._id;

      await Promise.all(selectedItems.map(async (item) => {
        const prod = getProductById(item.productId);
        const price = prod.discounted_price || prod.price || 0;

        await axios.post(
          'http://localhost:4001/api/orderDetails/create',
          {
            orderId,
            productId: item.productId,
            paymentId,
            quantity: item.quantity,
            totalPrice: price * item.quantity
          },
          { headers: { Authorization: `Bearer ${token}` } }
        );
      }));

      alert('Đặt hàng thành công!');
    } catch (error) {
      console.error('Lỗi đặt hàng:', error);
      alert('Đặt hàng thất bại!');
    }
  };

  return (
    <div className="payment-container">
      <div className="address-section">
        <h3>Địa chỉ nhận hàng</h3>
        <form className="address-form">
          <input type="text" placeholder="Họ và tên" value={shippingInfo.name} onChange={(e) => setShippingInfo({ ...shippingInfo, name: e.target.value })} required />
          <input type="text" placeholder="SĐT" value={shippingInfo.phone} onChange={(e) => setShippingInfo({ ...shippingInfo, phone: e.target.value })} required />
          <input type="text" placeholder="Tỉnh / Thành phố" value={shippingInfo.province} onChange={(e) => setShippingInfo({ ...shippingInfo, province: e.target.value })} required />
          <input type="text" placeholder="Địa chỉ" value={shippingInfo.address} onChange={(e) => setShippingInfo({ ...shippingInfo, address: e.target.value })} required />
        </form>
      </div>

      <div className="payment-method-section">
        <h3>Phương thức thanh toán</h3>
        {['cod', 'bank', 'credit'].map(method => (
          <label key={method}>
            <input
              type="radio"
              name="paymentMethod"
              value={method}
              checked={paymentMethod === method}
              onChange={() => setPaymentMethod(method)}
            />
            {method === 'cod' && 'COD'}
            {method === 'bank' && 'Chuyển khoản'}
            {method === 'credit' && 'Thẻ tín dụng'}
          </label>
        ))}
      </div>

      <div className="product-summary">
        <h3>Sản phẩm</h3>
        {selectedItems.map((item, i) => {
          const prod = getProductById(item.productId);
          const price = prod?.discounted_price || prod?.price || 0;
          return (
            <div className="summary-item" key={i}>
              <img src={prod.image?.[0]} alt={prod.name} />
              <div>
                <p>{prod.name}</p>
                <p>Số lượng: {item.quantity}</p>
                <p>Giá: đ{(price * item.quantity).toLocaleString('vi-VN')}</p>
              </div>
            </div>
          );
        })}
      </div>

      <div className="payment-summary">
        <h3>Tổng thanh toán</h3>
        <p>đ{totalPrice.toLocaleString('vi-VN')}</p>
        <button className="btn-confirm" onClick={handleOrderSubmit}>Đặt hàng</button>
      </div>
    </div>
  );
};
