import React, { useState, useEffect } from 'react';
import { useAppContext } from '../../context/AppContext';
import axios from 'axios';
import './order.css';

export const Order = () => {
    const { user } = useAppContext();
    const [cartItems, setCartItems] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchCart = async () => {
            try {
                const res = await axios.get(`http://localhost:3003/api/cart/user/${user.id}`);
                setCartItems(res.data);
            } catch (error) {
                console.error('Lỗi khi lấy giỏ hàng:', error);
            } finally {
                setLoading(false);
            }
        };

        if (user?.id) {
            fetchCart();
        }
    }, [user]);

    return (
        <div className="payment-container">
            <div className="address-section">
                <h3>Địa chỉ nhận hàng</h3>
                <form className="address-form">
                    <input type="text" placeholder="Họ và tên" name="name" required />
                    <input type="number" placeholder="Phone" name="phone" required />
                    <input type="text" placeholder="Tỉnh / Thành phố" name="province" required />
                    <input type="text" placeholder="Địa chỉ chi tiết" name="address" required />
                </form>
            </div>

            <div className="payment-method-section">
                <h3>Phương thức thanh toán</h3>
                <label>
                    <input type="radio" name="paymentMethod" value="cod" /> Thanh toán khi nhận hàng (COD)
                </label>
                <label>
                    <input type="radio" name="paymentMethod" value="bank" /> Thanh toán qua ngân hàng
                </label>
                <label>
                    <input type="radio" name="paymentMethod" value="credit" /> Thanh toán bằng thẻ tín dụng
                </label>
            </div>

            <div className="product-status">
                <h3>Trạng thái</h3>
                <span>Pending</span>
            </div>

            <div className="product-summary">
                <h3>Sản phẩm</h3>
                {loading ? (
                    <p>Đang tải...</p>
                ) : cartItems.length === 0 ? (
                    <p>Giỏ hàng trống</p>
                ) : (
                    cartItems.map((item) => (
                        <div className="summary-item" key={item._id}>
                            <img src={item.productId.image[0]} alt={item.productId.name} />
                            <div>
                                <p>{item.productId.name}</p>
                                <p>Số lượng: {item.quantity}</p>
                                <p>Giá: {item.productId.price.toLocaleString()} đ</p>
                            </div>
                        </div>
                    ))
                )}
            </div>

            <div className="payment-summary">
                <h3>Tổng thanh toán</h3>
                <p>
                    {cartItems
                        .reduce((total, item) => total + item.quantity * item.productId.price, 0)
                        .toLocaleString()} đ
                </p>
                <button className="btn-confirm">Đặt hàng</button>
            </div>
        </div>
    );
};
