import React from 'react';
import './sellershop.css';

export const SellerShop = ({ seller }) => {
  if (!seller) return <div>Loading shop info...</div>;

  return (
    <div className="shop-header">
      <div className="shop-info-left">
        <img
          className="shop-avatar"
          src={seller.image?.[0] || 'default-avatar.png'}
          alt="Shop Avatar"
        />
        <div className="shop-details">
          <h2 className="shop-name">{seller.storeName}</h2>
          <p className="shop-online">Online: 7 minutes ago</p>
