import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './shoppages.css';
import { useParams } from 'react-router-dom';

export const ShopPages = () => {
    const { sellerId } = useParams();
    const [shopInfo, setShopInfo] = useState(null);

    useEffect(() => {
        const fetchShopInfo = async () => {
            try {
                const res = await axios.get(`http://localhost:4003/api/product/sellers/${sellerId}`);
                console.log('Shop info:', res.data);  // Log kết quả API trả về
                if (res.data && res.data.length > 0) {
                    setShopInfo(res.data[0].sellerId);  // Chỉ lấy thông tin từ sellerId của sản phẩm đầu tiên
                }
            } catch (error) {
                console.error('Lỗi khi lấy thông tin seller:', error);
            }
        };

        if (sellerId) {
            fetchShopInfo();
        }
    }, [sellerId]);

    // Kiểm tra nếu chưa có dữ liệu, hiển thị Loading
    if (!shopInfo) return <div>Loading...</div>;

    return (
        <div className="shop-page">
            <div className="shopIno">
                <div className="shop-info-left">
                    <img
                        className="shop-avatar"
                        src={shopInfo?.image?.[0] || "/default-avatar.png"}  // Hiển thị avatar hoặc ảnh mặc định
                        alt="Shop Avatar"
                    />
                    <div className="shop-details">
                        <h2 className="shop-name">{shopInfo?.storeName || "Tên shop"}</h2>
                        <p className="shop-online">Online: 7 minutes ago</p>
                    </div>
                </div>
                <div className="shop-info-right">
                    <p className="stat-title">Địa chỉ: {shopInfo?.storeAddress || "Địa chỉ chưa có"}</p>
                    <p className="stat-title">Số điện thoại: {shopInfo?.phone || "Số điện thoại chưa có"}</p>
                </div>
            </div>

            {/* Danh mục của shop (có thể lấy thêm dữ liệu từ backend nếu cần) */}
            <div className="shop__category">
                <ul>
                    <li>Danh mục 1</li>
                    <li>Danh mục 2</li>
                    <li>Danh mục 3</li>
                </ul>
            </div>
        </div>
    );
};
