import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './categoryAdmin.css';

export const CategoryAdmin = () => {
  const [categories, setCategories] = useState([]);
  const [error, setError] = useState('');
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [editingId, setEditingId] = useState(null);

  const fetchCategories = async () => {
    try {
      const res = await axios.get('http://localhost:3001/api/category/all');
      setCategories(res.data || []);
      setError('');
    } catch (err) {
      console.error('Lỗi khi lấy dữ liệu:', err.message);
      setError('Không thể lấy danh mục.');
    }
  };

  useEffect(() => {
    fetchCategories();
  }, []);

  // Thêm mới hoặc cập nhật danh mục
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editingId) {
        // Cập nhật danh mục
        await axios.put(`http://localhost:3001/api/category/${editingId}`, {
          name,
          description
        });
        setEditingId(null);
      } else {
        // Thêm mới danh mục
        await axios.post('http://localhost:3001/api/category', {
          name,
          description
        });
      }
      setName('');
      setDescription('');
      fetchCategories();
      setError('');
    } catch (err) {
      console.error('Lỗi khi lưu danh mục:', err.message);
      setError('Lỗi khi lưu danh mục');
    }
  };

  // Xóa danh mục
  const handleDelete = async (id) => {
    if (!window.confirm('Bạn có chắc muốn xóa danh mục này không?')) return;
    try {
      await axios.delete(`http://localhost:3001/api/category/${id}`);
      fetchCategories();
      setError('');
    } catch (err) {
      console.error('Lỗi khi xóa danh mục:', err.message);
      setError('Lỗi khi xóa danh mục');
    }
  };

  // Chọn danh mục để sửa
  const handleEdit = (category) => {
    setEditingId(category._id);
    setName(category.name);
    setDescription(category.description || '');
  };

  // Hủy sửa
  const handleCancelEdit = () => {
    setEditingId(null);
    setName('');
    setDescription('');
    setError('');
  };

  return (
    <div className="category-admin-container">
      <h2>Quản lý danh mục</h2>

      {error && <p className="error-message">{error}</p>}

      <form onSubmit={handleSubmit} className="category-form">
        <input
          type="text"
          placeholder="Tên danh mục"
          value={name}
          onChange={e => setName(e.target.value)}
          required
        />
        <textarea
          placeholder="Mô tả (tuỳ chọn)"
          value={description}
          onChange={e => setDescription(e.target.value)}
          rows={3}
        />
        <button type="submit">{editingId ? 'Cập nhật' : 'Thêm mới'}</button>
        {editingId && (
          <button type="button" onClick={handleCancelEdit} className="cancel-btn">
            Hủy
          </button>
        )}
      </form>

      <ul className="category-list">
        {categories.length > 0 ? (
          categories.map(category => (
            <li key={category._id} className="category-item">
              <div onClick={() => handleEdit(category)} style={{cursor: 'pointer'}}>
                <strong>{category.name}</strong><br />
                <small>{category.description}</small>
              </div>
              <button onClick={() => handleDelete(category._id)} className="delete-btn">
                Xóa
              </button>
            </li>
          ))
        ) : (
          <li>Không có danh mục nào.</li>
        )}
      </ul>
    </div>
  );
};
