import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './shoppages.css';
import { useParams } from 'react-router-dom';

export const ShopPages = () => {
    const { sellerId } = useParams();
    const [products, setProducts] = useState([]);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const res = await axios.get(`http://localhost:4003/api/product/sellers/${sellerId}`);
                console.log("Dữ liệu trả về:", res.data); // Xem dữ liệu trả về từ API
                setProducts(res.data);
            } catch (error) {
                console.error('Lỗi khi lấy sản phẩm:', error);
            }
        };


        fetchProducts();
    }, [sellerId]);

    if (products.length === 0) {
        return <div>Loading products...</div>;
    }

    return (
        <div className="shop-page">
            <div className='shopIno'>
                <div className="shop-info-left">
                    <img
                        className="shop-avatar"
                        src=""
                        alt="Shop Avatar"
                    />
                    <div className="shop-details">
                        <h2 className="shop-name">Ten Shope</h2>
                        <p className="shop-online">Online: 7 minutes ago</p>
                    </div>
                </div>
            </div>
                 <div className='shop__category'>
                    <ul>
                        <li>Danh muc 1</li>
                        <li>Danh muc 2</li>
                        <li>Danh muc 3</li>
                    </ul>
                </div>
        </div>
    );
};
