import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './shoppages.css';
import { useParams } from 'react-router-dom';

export const ShopPages = () => {
    const { sellerId } = useParams();
    const [shopInfo, setShopInfo] = useState(null);

    useEffect(() => {
        const fetchShopInfo = async () => {
            try {
                const res = await axios.get(`http://localhost:4003/api/sellers/${sellerId}`);
                console.log("Shop info:", res.data);
                setShopInfo(res.data);
            } catch (error) {
                console.error('Lỗi khi lấy thông tin seller:', error);
            }
        };

        if (sellerId) {
            fetchShopInfo();
        }
    }, [sellerId]);

    if (!shopInfo) return <div>Loading...</div>;

    return (
        <div className="shop-page">
            <div className="shopIno">
                <div className="shop-info-left">
                    <img
                        className="shop-avatar"
                        src={shopInfo.image || "/default-avatar.png"}
                        alt="Shop Avatar"
                    />
                    <div className="shop-details">
                        <h2 className="shop-name">{shopInfo.storeName}</h2>
                        <p className="shop-online">Online: 7 minutes ago</p>
                    </div>
                </div>
                <div className="shop-info-right">
                    <p className="stat-title">Địa chỉ: {shopInfo.storeAddress}</p>
                    <p className="stat-title">Số điện thoại: {shopInfo.phone}</p>
                </div>
            </div>

            <div className="shop__category">
                <ul>
                    <li>Danh mục 1</li>
                    <li>Danh mục 2</li>
                    <li>Danh mục 3</li>
                </ul>
            </div>
        </div>
    );
};



