import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './shoppages.css';
import { useParams } from 'react-router-dom';

export const ShopPages = () => {
    const { sellerId } = useParams();
    const [products, setProducts] = useState([]);

    useEffect(() => {
        const fetchProducts = async () => {
            try {
                const res = await axios.get(`http://localhost:4003/api/product/sellers/${sellerId}`);
                console.log("Dữ liệu trả về:", res.data); // Xem dữ liệu trả về từ API
                setProducts(res.data);
            } catch (error) {
                console.error('Lỗi khi lấy sản phẩm:', error);
            }
        };


        fetchProducts();
    }, [sellerId]);

    if (products.length === 0) {
        return <div>Loading products...</div>;
    }

    return (
        <div className="shop-page">
            <h2>Danh sách sản phẩm của shop</h2>
            {products.length > 0 ? (
                <div className="product-list">
                    {products.map((item) => (
                        <div key={item._id} className="product-card">
                            <img src={item.images[0] || 'default-image.jpg'} alt={item.name} />
                            <h4>{item.name}</h4>
                            <p>{item.price} VND</p>
                            <p>Danh mục: {item.categoryId?.name || 'Chưa có danh mục'}</p>
                        </div>
                    ))}
                </div>
            ) : (
                <p>Không có sản phẩm trong cửa hàng này.</p>
            )}
        </div>
    );
};
