import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './cart.css';
import axios from 'axios';
import { useAppContext } from '../../context/AppContext';

export const Cart = () => {
  const navigate = useNavigate();
  const { user } = useAppContext();
  const [cartItems, setCartItems] = useState([]);
  const [selectedItems, setSelectedItems] = useState([]);

  useEffect(() => {
    const fetchCart = async () => {
      if (!user) {
        alert('Vui lòng đăng nhập để xem giỏ hàng!');
        navigate('/login');
        return;
      }

      try {
        const res = axios.get(`http://localhost:3003/api/cart/user/${user.id}`);

        setCartItems(res.data.cartItems); // [{ productId, productName, productPrice, productImage, quantity }]
        setSelectedItems(res.data.cartItems.map(item => item.productId));
      } catch (error) {
        console.error('Lỗi khi lấy giỏ hàng:', error);
      }
    };

    fetchCart();
  }, [user, navigate]);

  const handleSelectAll = (checked) => {
    if (checked) {
      setSelectedItems(cartItems.map(item => item.productId));
    } else {
      setSelectedItems([]);
    }
  };

  const handleSelectItem = (productId) => {
    setSelectedItems((prev) =>
      prev.includes(productId)
        ? prev.filter(id => id !== productId)
        : [...prev, productId]
    );
  };

  const updateQuantity = async (productId, newQuantity) => {
    if (newQuantity < 1) return;

    try {
      await axios.put(`http://localhost:3003/api/cart/update`, {
        userId: user.id,
        productId,
        quantity: newQuantity,
      });

      setCartItems((prev) =>
        prev.map(item =>
          item.productId === productId
            ? { ...item, quantity: newQuantity }
            : item
        )
      );
    } catch (err) {
      console.error('Lỗi cập nhật số lượng:', err);
    }
  };

  const deleteItem = async (productId) => {
    try {
      await axios.delete(`http://localhost:3003/api/cart/delete/${user.id}/${productId}`);
      setCartItems(prev => prev.filter(item => item.productId !== productId));
      setSelectedItems(prev => prev.filter(id => id !== productId));
    } catch (err) {
      console.error('Lỗi xoá sản phẩm:', err);
    }
  };

  const totalSelectedPrice = cartItems
    .filter(item => selectedItems.includes(item.productId))
    .reduce((sum, item) => sum + item.productPrice * item.quantity, 0);

  const handleCheckout = async () => {
    try {
      const selected = cartItems.filter(item => selectedItems.includes(item.productId));
      const orderData = {
        customerId: user.id,
        items: selected.map(item => ({
          productId: item.productId,
          quantity: item.quantity,
        })),
      };

      await axios.post('http://localhost:3004/api/order/create', orderData);

      alert('Đặt hàng thành công!');
      navigate('/order-success');
    } catch (error) {
      console.error('Lỗi đặt hàng:', error);
      alert('Không thể hoàn tất đơn hàng');
    }
  };

  return (
    <div className="cart">
      <h2>Giỏ hàng của bạn</h2>

      {cartItems.length === 0 ? (
        <div className="empty-cart">
          <p>Giỏ hàng trống</p>
          <Link to="/" className="btn-continue">Tiếp tục mua sắm</Link>
        </div>
      ) : (
        <>
          <div className="cart-header">
            <input
              type="checkbox"
              checked={selectedItems.length === cartItems.length}
              onChange={(e) => handleSelectAll(e.target.checked)}
            />
            <span>Chọn tất cả</span>
          </div>

          <div className="cart-items">
            {cartItems.map((item, index) => (
              <div className="cart-item" key={index}>
                <input
                  type="checkbox"
                  checked={selectedItems.includes(item.productId)}
                  onChange={() => handleSelectItem(item.productId)}
                />
                <img src={item.productImage} alt={item.productName} />
                <div className="cart-item-info">
                  <h4>{item.productName}</h4>
                  <p>Giá: đ{item.productPrice.toLocaleString()}</p>

                  <div className="quantity-control">
                    <button onClick={() => updateQuantity(item.productId, item.quantity - 1)}>-</button>
                    <input type="text" value={item.quantity} readOnly />
                    <button onClick={() => updateQuantity(item.productId, item.quantity + 1)}>+</button>
                  </div>

                  <p>Thành tiền: đ{(item.productPrice * item.quantity).toLocaleString()}</p>

                  <button className="btn-delete" onClick={() => deleteItem(item.productId)}>Xóa</button>
                </div>
              </div>
            ))}
          </div>

          <div className="cart-footer">
            <div className="footer-left">
              <input
                type="checkbox"
                checked={selectedItems.length === cartItems.length}
                onChange={(e) => handleSelectAll(e.target.checked)}
              />
              <span>Chọn tất cả ({selectedItems.length})</span>
            </div>

            <div className="footer-right">
              <p>
                Tổng thanh toán:{' '}
                <span>đ{totalSelectedPrice.toLocaleString()}</span>
              </p>
              <button className="btn-buy" onClick={handleCheckout}>Mua hàng</button>
            </div>
          </div>
        </>
      )}
    </div>
  );
};
