import React, { useState, useEffect } from 'react';
import { useAppContext } from '../../context/AppContext';
import axios from 'axios';
import './order.css';

export const Order = () => {
  const { user } = useAppContext();
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [totalAmount, setTotalAmount] = useState(0);
  const [shippingCost, setShippingCost] = useState(0); // Dùng để lưu chi phí vận chuyển
  const [selectedTransport, setSelectedTransport] = useState(""); // Phương thức vận chuyển đã chọn

  useEffect(() => {
    const fetchCart = async () => {
      try {
        const res = await axios.get(`http://localhost:3003/api/cart/user/${user.id}`);
        setCartItems(res.data);
        const total = res.data.reduce(
          (total, item) => total + item.quantity * item.productId.price,
          0
        );
        setTotalAmount(total);
      } catch (error) {
        console.error('Lỗi khi lấy giỏ hàng:', error);
      } finally {
        setLoading(false);
      }
    };

    if (user?.id) {
      fetchCart();
    }
  }, [user]);

  const handleTransportChange = (event) => {
    const transport = event.target.value;
    let newShippingCost = 0;
    switch (transport) {
      case 'GHN':
        newShippingCost = 35000;
        break;
      case 'GHTK':
        newShippingCost = 30000;
        break;
      case 'ViettelPost':
        newShippingCost = 25000;
        break;
      default:
        newShippingCost = 0;
    }
    setSelectedTransport(transport);
    setShippingCost(newShippingCost);
    setTotalAmount((prevTotal) => prevTotal + newShippingCost);
  };

  const handleOrderSubmit = async () => {
    const orderData = {
      totalOrder: totalAmount,
      shippingInfo: {
        name: 'Nguyễn Văn A', // Giả sử bạn lấy thông tin từ form
        phone: '0123456789',
        province: 'Hà Nội',
        address: 'Số 123, Đường ABC'
      },
      transportId: selectedTransport,
      userId: user.id
    };

    try {
      await axios.post('http://localhost:4000/api/order/create', orderData);
      alert('Đặt hàng thành công!');
    } catch (error) {
      console.error('Lỗi khi tạo đơn hàng:', error);
      alert('Đặt hàng thất bại!');
    }
  };

  return (
    <div className="payment-container">
      <div className="address-section">
        <h3>Địa chỉ nhận hàng</h3>
        <form className="address-form">
          <input type="text" placeholder="Họ và tên" name="name" required />
          <input type="number" placeholder="Phone" name="phone" required />
          <input type="text" placeholder="Tỉnh / Thành phố" name="province" required />
          <input type="text" placeholder="Địa chỉ chi tiết" name="address" required />
        </form>
      </div>

      <div className="payment-method-section">
        <h3>Phương thức thanh toán</h3>
        <label>
          <input type="radio" name="paymentMethod" value="cod" /> Thanh toán khi nhận hàng (COD)
        </label>
      </div>

      <div className="transport-method">
        <label>
          <input
            type="radio"
            name="transportMethod"
            value="GHN"
            onChange={handleTransportChange}
          />
          GHN: 35000 Đ
        </label>
        <label>
          <input
            type="radio"
            name="transportMethod"
            value="GHTK"
            onChange={handleTransportChange}
          />
          GHTK: 30000 Đ
        </label>
        <label>
          <input
            type="radio"
            name="transportMethod"
            value="ViettelPost"
            onChange={handleTransportChange}
          />
          Viettel Post: 25000 Đ
        </label>
      </div>

      <div className="product-summary">
        <h3>Sản phẩm</h3>
        {loading ? (
          <p>Đang tải...</p>
        ) : cartItems.length === 0 ? (
          <p>Giỏ hàng trống</p>
        ) : (
          cartItems.map((item) => (
            <div className="summary-item" key={item._id}>
              <img src={item.productId.image[0]} alt={item.productId.name} />
              <div>
                <p>{item.productId.name}</p>
                <p>Số lượng: {item.quantity}</p>
                <p>Giá: {item.productId.price.toLocaleString()} đ</p>
              </div>
            </div>
          ))
        )}
      </div>

      <div className="payment-summary">
        <h3>Tổng thanh toán</h3>
        <p>{totalAmount.toLocaleString()} đ</p>
        <button className="btn-confirm" onClick={handleOrderSubmit}>
          Đặt hàng
        </button>
      </div>
    </div>
  );
};
