import React, { useEffect, useState } from 'react';
import './productdetails.css';
import { useParams, Link } from 'react-router-dom';
import { Button, Rate, Row, Col } from 'antd';
import { toSlug } from '../../utils/toSlug';
import { useAppContext } from '../../context/AppContext';

export const ProductDetails = () => {
  const { product, navigate } = useAppContext();
  const { id } = useParams();
  const [relatedProducts, setRelatedProducts] = useState([]);
  const [thumbnail, setThumbnail] = useState(product.images[0]);


  const currentProduct = product.find((item) => item.id === Number(id));

  useEffect(() => {
    if (product.length > 0) {
      let productsCopy = product.slice();
      productsCopy = productsCopy.filter((item) => currentProduct.category === item.category);
      setRelatedProducts(productsCopy.slice(0, 5));
    }
  }, [product, currentProduct]);

  useEffect(() => {
    setThumbnail(currentProduct?.img?.[0] || null);
  }, [currentProduct]);


  if (!currentProduct) return <p>Đang tải sản phẩm...</p>;

  return (
    <div className="product-details">
      <nav>
        <p className="breadcrumb">
          <Link to="/">Trang chủ</Link> /
          <Link to={`/product/${toSlug(currentProduct.category)}`}>Sản phẩm</Link> /
          <span>{currentProduct.category}</span> /
          <span className="highlight">{currentProduct.name}</span>
        </p>
      </nav>

      <div className="flex gap-3">
        <div className="flex flex-col gap-3">
          {currentProduct?.img?.map((image, index) => (
            <div
              key={index}
              onClick={() => setThumbnail(image)}
              className={`border max-w-24 border-gray-500/30 rounded overflow-hidden cursor-pointer ${thumbnail === image ? 'ring-2 ring-blue-500' : ''
                }`}
            >
              <img src={image} alt={`Thumbnail ${index + 1}`} className="object-cover w-24 h-24" />
            </div>
          ))}
        </div>

        <div className="border border-gray-500/30 max-w-[400px] rounded overflow-hidden">
          <img src={thumbnail} alt="Selected product" className="object-contain w-full h-[400px]" />
        </div>
      </div>

      <div className="product-main">
        <img src={thumbnail} alt={currentProduct.name} />
        <h1>{currentProduct.name}</h1>
        <p>{currentProduct.price}</p>
        <Rate disabled defaultValue={currentProduct.rating} />
      </div>
    </div>
  );
};
