import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './cart.css';

export const Cart = () => {
  const navigate = useNavigate();
  const [cartItems, setCartItems] = useState([]);
  const [selectedItems, setSelectedItems] = useState([]);
  const userData = localStorage.getItem('user');
  const parsedUser = userData ? JSON.parse(userData) : null;
  useEffect(() => {
    if (!parsedUser) return;

    const fetchCartWithProductDetails = async () => {
      try {
        const cartRes = await axios.get(`http://localhost:3003/api/cart/user/${parsedUser.id}`);
        const cartData = cartRes.data;

        const enrichedCart = await Promise.all(
          cartData.map(async (item) => {
            try {
              const productRes = await axios.get(`http://localhost:4003/api/product/${item.productId}`);
              return {
                ...item,
                product: productRes.data
              };
            } catch (error) {
              console.error('Lỗi lấy sản phẩm:', error);
              return item;
            }
          })
        );

        setCartItems(enrichedCart);
        setSelectedItems(enrichedCart.map(item => item.productId));
      } catch (err) {
        console.error('Lỗi lấy giỏ hàng:', err);
      }
    };

    fetchCartWithProductDetails();
  }, [parsedUser?.id]);  


  const totalSelectedPrice = cartItems
    .filter(item => selectedItems.includes(item.productId))
    .reduce((sum, item) => sum + (item.product?.price || 0) * item.quantity, 0);

  return (
    <div className="cart">
      <h2>Giỏ hàng của bạn</h2>

      {cartItems.length === 0 ? (
        <div className="empty-cart">
          <p>Giỏ hàng trống</p>
          <Link to="/" className="btn-continue">Tiếp tục mua sắm</Link>
        </div>
      ) : (
        <>
          <div className="cart-header">
            <input
              type="checkbox"
              checked={selectedItems.length === cartItems.length}
              onChange={() => {
                if (selectedItems.length === cartItems.length) {
                  setSelectedItems([]);
                } else {
                  setSelectedItems(cartItems.map(item => item.productId));
                }
              }}
            />
            <span>Chọn tất cả</span>
          </div>

          <div className="cart-items">
            {cartItems.map((item, index) => (
              <div className="cart-items">
                {cartItems.map((item, index) => (
                  <div className="cart-item" key={index}>
                    <input
                      type="checkbox"
                      checked={selectedItems.includes(item.productId)}
                      onChange={() => {
                        if (selectedItems.includes(item.productId)) {
                          setSelectedItems(selectedItems.filter(id => id !== item.productId));
                        } else {
                          setSelectedItems([...selectedItems, item.productId]);
                        }
                      }}
                    />
                    {/* Chỉ hiển thị một hình ảnh đại diện cho sản phẩm */}
                    <img
                      src={item.product?.image[0]} // Lấy hình ảnh đầu tiên trong mảng
                      alt={item.product?.name || 'Sản phẩm'}
                    />
                    <div className="cart-item-info">
                      <h4>{item.product?.name || 'Tên sản phẩm'}</h4>
                      <p>Giá: đ{(item.product?.price || 0).toLocaleString()}</p>
                      <div className="quantity-control">
                        <button>-</button>
                        <input type="text" value={item.quantity} readOnly />
                        <button>+</button>
                      </div>
                      <p>Thành tiền: đ{((item.product?.price || 0) * item.quantity).toLocaleString()}</p>
                      <button className="btn-delete">Xóa</button>
                    </div>
                  </div>
                ))}
              </div>

              // <div className="cart-item" key={index}>
              //   <input
              //     type="checkbox"
              //     checked={selectedItems.includes(item.productId)}
              //     onChange={() => {
              //       if (selectedItems.includes(item.productId)) {
              //         setSelectedItems(selectedItems.filter(id => id !== item.productId));
              //       } else {
              //         setSelectedItems([...selectedItems, item.productId]);
              //       }
              //     }}
              //   />
              //   <img src={item.product?.image} alt={item.product?.name || 'Sản phẩm'} />
              //   <div className="cart-item-info">
              //     <h4>{item.product?.name || 'Tên sản phẩm'}</h4>
              //     <p>Giá: đ{(item.product?.price || 0).toLocaleString()}</p>
              //     <div className="quantity-control">
              //       <button>-</button>
              //       <input type="text" value={item.quantity} readOnly />
              //       <button>+</button>
              //     </div>
              //     <p>Thành tiền: đ{((item.product?.price || 0) * item.quantity).toLocaleString()}</p>
              //     <button className="btn-delete">Xóa</button>
              //   </div>
              // </div>
            ))}
          </div>

          <div className="cart-footer">
            <div className="footer-left">
              <input
                type="checkbox"
                checked={selectedItems.length === cartItems.length}
                onChange={() => {
                  if (selectedItems.length === cartItems.length) {
                    setSelectedItems([]);
                  } else {
                    setSelectedItems(cartItems.map(item => item.productId));
                  }
                }}
              />
              <span>Chọn tất cả ({selectedItems.length})</span>
            </div>

            <div className="footer-right">
              <p>
                Tổng thanh toán:
                <span> đ{totalSelectedPrice.toLocaleString()}</span>
              </p>
              <button className="btn-buy">Mua hàng</button>
            </div>
          </div>
        </>
      )}
    </div>
  );
};
