import React, { useState } from 'react';
import './addProducts.css';
import axios from 'axios';
import { useAppContext } from '../../context/AppContext';
import { useNavigate } from 'react-router-dom';

export const AddProducts = () => {
  const { categories, user, seller } = useAppContext();
  const navigate = useNavigate();

  const [images, setImages] = useState([]);
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [category, setCategory] = useState('');
  const [status, setStatus] = useState('in_stock');
  const [price, setPrice] = useState('');
  const [quantity, setQuantity] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Handle image input change
  const handleImageChange = (e, index) => {
    const files = [...images];
    files[index] = e.target.files[0];
    setImages(files);
  };

  // Submit form handler
  const handleSubmit = async (e) => {
    e.preventDefault();

    const token = localStorage.getItem('token');
    const userId = user ? user.id : null;     // Sửa user.id thành user._id
    const sellerId = seller ? seller._id : null; // Sửa seller.id thành seller._id

    if (!token) {
      alert('Bạn chưa đăng nhập hoặc thiếu token!');
      return;
    }

    if (!userId) {
      alert('UserId không hợp lệ!');
      return;
    }

    // Validate required fields trước submit
    if (!name || !description || !category || !price || !quantity) {
      alert('Vui lòng điền đầy đủ thông tin sản phẩm!');
      return;
    }

    const formData = new FormData();
    formData.append('name', name);
    formData.append('describe', description);
    formData.append('categoryId', category);
    formData.append('status', status);
    formData.append('price', price);
    formData.append('quantity', quantity);
    formData.append('userId', userId);

    // Nếu sellerId tồn tại mới thêm
    if (sellerId) {
      formData.append('sellerId', sellerId);
    }

    images.forEach((file) => {
      if (file) {
        formData.append('image', file);
      }
    });

    try {
      setIsSubmitting(true);
      await axios.post('http://localhost:4003/api/product/create', formData, {
        headers: {
          'Authorization': `Bearer ${token}`,
          'Content-Type': 'multipart/form-data'
        }
      });

      alert('Thêm sản phẩm thành công!');

      // Reset form sau khi thêm thành công
      setName('');
      setDescription('');
      setCategory('');
      setStatus('in_stock');
      setPrice('');
      setQuantity('');
      setImages([]);

      navigate('/seller');
    } catch (error) {
      console.error('Lỗi khi thêm sản phẩm:', JSON.stringify(error.response?.data, null, 2) || error.message);
      alert('Có lỗi xảy ra khi thêm sản phẩm: ' + (JSON.stringify(error.response?.data, null, 2) || error.message));
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="product-form">
      <form className="form-container" onSubmit={handleSubmit}>
        <div className="form-group">
          <label className="form-label">Hình ảnh sản phẩm</label>
          <div className="image-upload-group">
            {Array(6).fill('').map((_, index) => (
              <label key={index} htmlFor={`image${index}`}>
                <input
                  accept="image/*"
                  type="file"
                  id={`image${index}`}
                  name="images"
                  hidden
                  onChange={(e) => handleImageChange(e, index)}
                />
                <img
                  className="upload-preview"
                  src={
                    images[index]
                      ? URL.createObjectURL(images[index])
                      : "https://raw.githubusercontent.com/prebuiltui/prebuiltui/main/assets/e-commerce/uploadArea.png"
                  }
                  alt="upload"
                />
              </label>
            ))}
          </div>
        </div>

        <div className="form-group">
          <label htmlFor="name" className="form-label">Tên sản phẩm</label>
          <input
            id="name"
            type="text"
            className="form-input"
            placeholder="Nhập vào đây"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="description" className="form-label">Mô tả sản phẩm</label>
          <textarea
            id="description"
            className="form-input"
            rows="4"
            placeholder="Nhập vào đây"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
          ></textarea>
        </div>

        <div className="form-group">
          <label htmlFor="category" className="form-label">Danh mục</label>
          <select
            id="category"
            className="form-input"
            value={category}
            onChange={(e) => setCategory(e.target.value)}
            required
          >
            <option value="">Chọn danh mục</option>
            {categories.map(cat => (
              <option key={cat._id} value={cat._id}>{cat.name}</option>
            ))}
          </select>

          <label htmlFor="status" className="form-label">Trạng thái</label>
          <select
            id="status"
            className="form-input"
            value={status}
            onChange={(e) => setStatus(e.target.value)}
            required
          >
            <option value="in_stock">Còn hàng</option>
            <option value="out_of_stock">Hết hàng</option>
          </select>
        </div>

        <div className="form-row">
          <div className="form-group small">
            <label htmlFor="price" className="form-label">Giá sản phẩm</label>
            <input
              id="price"
              type="number"
              className="form-input"
              placeholder="0"
              value={price}
              onChange={(e) => setPrice(e.target.value)}
              required
              min={0}
            />
          </div>
          <div className="form-group small">
            <label htmlFor="quantity" className="form-label">Số lượng</label>
            <input
              id="quantity"
              type="number"
              className="form-input"
              placeholder="0"
              value={quantity}
              onChange={(e) => setQuantity(e.target.value)}
              required
              min={0}
            />
          </div>
        </div>

        <button type="submit" className="submit-btn" disabled={isSubmitting}>
          {isSubmitting ? 'Đang thêm...' : 'THÊM SẢN PHẨM'}
        </button>
      </form>
    </div>
  );
};
