import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './customerForm.css';

const CustomerForm = () => {
  const [formData, setFormData] = useState({
    _id: '',
    name: '',
    email: '',
    phone: '',
    birthday: '',
    gender: '',
  });

  // Load thông tin từ localStorage nếu có
  useEffect(() => {
    const storedCustomer = JSON.parse(localStorage.getItem('customer'));
    if (storedCustomer) {
      setFormData(storedCustomer);
    }
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (formData._id) {
        const res = await axios.put(`http://localhost:3002/api/customer/update/${formData._id}`, formData);
        alert('Cập nhật thông tin thành công!');
        localStorage.setItem('customer', JSON.stringify(res.data));
      } else {
        const res = await axios.post('http://localhost:3002/api/customer/create', formData);
        alert('Lưu thông tin thành công!');
        localStorage.setItem('customer', JSON.stringify(res.data));
        setFormData(res.data);
      }
    } catch (error) {
      console.error('Lỗi khi lưu/cập nhật thông tin khách hàng:', error);
      alert('Thao tác thất bại!');
    }
  };

  return (
    <div className="customer-form-overlay">
      <div className="customer-form">
        <h3>Thông tin khách hàng</h3>
        <form onSubmit={handleSubmit}>
          <input name="name" value={formData.name} placeholder="Họ tên" onChange={handleChange} required />
          <input name="email" value={formData.email} placeholder="Email" onChange={handleChange} required />
          <input name="phone" value={formData.phone} placeholder="Số điện thoại" onChange={handleChange} required />
          <input type="date" name="birthday" value={formData.birthday} onChange={handleChange} />
          <select name="gender" value={formData.gender} onChange={handleChange}>
            <option value="">Chọn giới tính</option>
            <option value="Nam">Nam</option>
            <option value="Nữ">Nữ</option>
            <option value="Khác">Khác</option>
          </select>
          <div className="actions">
            <button type="submit">Lưu</button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default CustomerForm;
