import React, { useState, useEffect } from 'react';
import { useAppContext } from '../../context/AppContext';
import axios from 'axios';
import './order.css';

export const Order = () => {
  const { user } = useAppContext();
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [shippingInfo, setShippingInfo] = useState({
    name: '',
    phone: '',
    province: '',
    address: '',
  });
  const [paymentMethod, setPaymentMethod] = useState('');
  const [transportMethod, setTransportMethod] = useState('');
  const [totalAmount, setTotalAmount] = useState(0);

  const transportCosts = {
    GHN: 35000,
    GHTK: 30000,
    ViettelPost: 25000,
  };

  useEffect(() => {
    const fetchCart = async () => {
      try {
        const res = await axios.get(`http://localhost:3003/api/cart/user/${user.id}`);
        setCartItems(res.data);
        const total = res.data.reduce(
          (total, item) => total + item.quantity * item.productId.price, 0
        );
        setTotalAmount(total);
      } catch (error) {
        console.error('Lỗi khi lấy giỏ hàng:', error);
      } finally {
        setLoading(false);
      }
    };

    if (user?.id) {
      fetchCart();
    }
  }, [user]);

  // Hàm tính lại tổng tiền khi phương thức giao hàng thay đổi
  const updateTotalAmount = (newTransportMethod) => {
    const transportCost = transportCosts[newTransportMethod] || 0;
    const newTotalAmount = cartItems.reduce(
      (total, item) => total + item.quantity * item.productId.price, 0
    ) + transportCost;
    setTotalAmount(newTotalAmount);
  };

  // Xử lý thay đổi phương thức vận chuyển
  const handleTransportChange = (e) => {
    setTransportMethod(e.target.value);
    updateTotalAmount(e.target.value);
  };

const handleOrderSubmit = async () => {
  try {
    // Lấy các ObjectId của phương thức thanh toán và vận chuyển từ cơ sở dữ liệu
    const payment = await axios.get(`http://localhost:3007/api/payment/${paymentMethod}`);
    const transport = await axios.get(`http://localhost:3005/api/transport/${transportMethod}`);

    const orderData = {
      totalOrder: totalAmount,
      customerId: user.id,
      paymentId: payment.data._id, // Chuyển paymentMethod thành ObjectId
      shippingInfo,
      transportId: transport.data._id, // Chuyển transportMethod thành ObjectId
      status: 'pending',
      userId: user.id // Thêm userId để lưu thông tin người dùng
    };

    const res = await axios.post('http://localhost:4000/api/order/create', orderData);
    alert('Đặt hàng thành công!');
  } catch (error) {
    console.error('Lỗi khi tạo đơn hàng:', error);
    alert('Đặt hàng không thành công');
  }
};

  return (
    <div className="payment-container">
      <div className="address-section">
        <h3>Địa chỉ nhận hàng</h3>
        <form className="address-form">
          <input
            type="text"
            placeholder="Họ và tên"
            name="name"
            value={shippingInfo.name}
            onChange={(e) => setShippingInfo({ ...shippingInfo, name: e.target.value })}
            required
          />
          <input
            type="number"
            placeholder="Phone"
            name="phone"
            value={shippingInfo.phone}
            onChange={(e) => setShippingInfo({ ...shippingInfo, phone: e.target.value })}
            required
          />
          <input
            type="text"
            placeholder="Tỉnh / Thành phố"
            name="province"
            value={shippingInfo.province}
            onChange={(e) => setShippingInfo({ ...shippingInfo, province: e.target.value })}
            required
          />
          <input
            type="text"
            placeholder="Địa chỉ chi tiết"
            name="address"
            value={shippingInfo.address}
            onChange={(e) => setShippingInfo({ ...shippingInfo, address: e.target.value })}
            required
          />
        </form>
      </div>

      <div className="payment-method-section">
        <h3>Phương thức thanh toán</h3>
        <label>
          <input
            type="radio"
            name="paymentMethod"
            value="cod"
            onChange={(e) => setPaymentMethod(e.target.value)}
          />
          Thanh toán khi nhận hàng (COD)
        </label>
      </div>

      <div className="transport-method">
        <label>
          <input
            type="radio"
            name="transportMethod"
            value="GHN"
            onChange={handleTransportChange}
          />
          GHN: 35000 Đ
        </label>
        <label>
          <input
            type="radio"
            name="transportMethod"
            value="GHTK"
            onChange={handleTransportChange}
          />
          GHTK: 30000 Đ
        </label>
        <label>
          <input
            type="radio"
            name="transportMethod"
            value="ViettelPost"
            onChange={handleTransportChange}
          />
          Viettel Post: 25000 Đ
        </label>
      </div>

      <div className="product-summary">
        <h3>Sản phẩm</h3>
        {loading ? (
          <p>Đang tải...</p>
        ) : cartItems.length === 0 ? (
          <p>Giỏ hàng trống</p>
        ) : (
          cartItems.map((item) => (
            <div className="summary-item" key={item._id}>
              <img src={item.productId.image[0]} alt={item.productId.name} />
              <div>
                <p>{item.productId.name}</p>
                <p>Số lượng: {item.quantity}</p>
                <p>Giá: {item.productId.price.toLocaleString()} đ</p>
              </div>
            </div>
          ))
        )}
      </div>

      <div className="payment-summary">
        <h3>Tổng thanh toán</h3>
        <p>{totalAmount.toLocaleString()} đ</p>
        <button className="btn-confirm" onClick={handleOrderSubmit}>Đặt hàng</button>
      </div>
    </div>
  );
};
