import React, { useEffect, useState } from 'react';
import './productdetails.css';
import { useAppContext } from '../../context/AppContext';
import { useParams, Link } from 'react-router-dom';
import { Button, Col, Row } from 'antd';

export const ProductDetails = () => {
  const { id } = useParams();
  const { product } = useAppContext();

  const [currentProduct, setCurrentProduct] = useState(null);
  const [thumbnail, setThumbnail] = useState(null);
  const [relatedProducts, setRelatedProducts] = useState([]);

  useEffect(() => {
    const foundProduct = product.find(item => item._id === id || item.slug === id); // tùy theo bạn dùng _id hay slug
    if (foundProduct) {
      setCurrentProduct(foundProduct);
      setThumbnail(foundProduct.image?.[0] || null);
      const related = product.filter(p => p.category === foundProduct.category && p._id !== foundProduct._id);
      setRelatedProducts(related.slice(0, 5));
    }
  }, [id, product]);

  if (!currentProduct) return <p>Loading...</p>;

  return (
    <>
      <Row>
        <Col span={24}>
          <ul>
            <Link to="/">Home</Link>
          </ul>
          <h2>{currentProduct.name}</h2>
          <img src={thumbnail} alt="thumbnail" style={{ width: '200px' }} />
          <p>{currentProduct.description}</p>
        </Col>
      </Row>

      <Row>
        <Col span={24}>
          <h3>Sản phẩm liên quan</h3>
          {relatedProducts.map(item => (
            <div key={item._id}>
              <Link to={`/product/${item.category}/${item._id}`}>{item.name}</Link>
            </div>
          ))}
        </Col>
      </Row>
    </>
  );
};
