import React, { useState, useEffect } from 'react';
import './addProducts.css';
import { useAppContext } from '../../context/appContext';

export const AddProducts = () => {
    const { categories } = useAppContext();
    const [productName, setProductName] = useState('');
    const [description, setDescription] = useState('');
    const [selectedCategory, setSelectedCategory] = useState('');
    const [price, setPrice] = useState('');
    const [offerPrice, setOfferPrice] = useState('');
    const [images, setImages] = useState([]);

    const handleImageChange = (event) => {
        const files = Array.from(event.target.files);
        if (files.length > 0) {
            setImages(prevImages => {
                const newImages = [];
                for (let i = 0; i < Math.min(files.length, 4); i++) {
                    newImages.push(URL.createObjectURL(files[i]));
                }
                return [...prevImages, ...newImages].slice(0, 4);
            });
        }
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        // Gửi dữ liệu sản phẩm lên server (chưa triển khai)
        console.log({
            name: productName,
            description,
            category: selectedCategory,
            price,
            offerPrice,
            images
        });
        // Reset form sau khi submit (tùy chọn)
        setProductName('');
        setDescription('');
        setSelectedCategory('');
        setPrice('');
        setOfferPrice('');
        setImages([]);
    };

    return (
        <div className="product-form">
            <form className="form-container" onSubmit={handleSubmit}>
                <div className="form-group">
                    <label className="form-label">Product Image</label>
                    <div className="image-upload-group">
                        {Array(4).fill('').map((_, index) => (
                            <label key={index} htmlFor={`image${index}`}>
                                <input
                                    accept="image/*"
                                    type="file"
                                    id={`image${index}`}
                                    hidden
                                    onChange={handleImageChange}
                                />
                                <img
                                    className="upload-preview"
                                    src={images[index] || "https://raw.githubusercontent.com/prebuiltui/prebuiltui/main/assets/e-commerce/uploadArea.png"}
                                    alt="upload"
                                />
                            </label>
                        ))}
                    </div>
                </div>

                <div className="form-group">
                    <label htmlFor="product-name" className="form-label">Product Name</label>
                    <input
                        id="product-name"
                        type="text"
                        className="form-input"
                        placeholder="Type here"
                        required
                        value={productName}
                        onChange={(e) => setProductName(e.target.value)}
                    />
                </div>

                <div className="form-group">
                    <label htmlFor="product-description" className="form-label">Product Description</label>
                    <textarea
                        id="product-description"
                        className="form-input"
                        rows="4"
                        placeholder="Type here"
                        value={description}
                        onChange={(e) => setDescription(e.target.value)}
                    ></textarea>
                </div>

                <div className="form-group">
                    <label htmlFor="category" className="form-label">Category</label>
                    <select
                        id="category"
                        className="form-input"
                        value={selectedCategory}
                        onChange={(e) => setSelectedCategory(e.target.value)}
                    >
                        <option value="">Select Category</option>
                        {categories.map(category => (
                            <option key={category._id} value={category._id}>{category.name}</option>
                        ))}
                    </select>
                </div>

                <div className="form-row">
                    <div className="form-group small">
                        <label htmlFor="product-price" className="form-label">Product Price</label>
                        <input
                            id="product-price"
                            type="number"
                            className="form-input"
                            placeholder="0"
                            required
                            value={price}
                            onChange={(e) => setPrice(e.target.value)}
                        />
                    </div>
                    <div className="form-group small">
                        <label htmlFor="offer-price" className="form-label">Offer Price</label>
                        <input
                            id="offer-price"
                            type="number"
                            className="form-input"
                            placeholder="0"
                            value={offerPrice}
                            onChange={(e) => setOfferPrice(e.target.value)}
                        />
                    </div>
                </div>

                <button type="submit" className="submit-btn">ADD</button>
            </form>
        </div>
    );
}
