import React, { useEffect, useState } from 'react';
import './productdetails.css';
import { Col, message } from 'antd'; // Import message để hiển thị thông báo
import { useParams, Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useAppContext } from '../../context/AppContext';
import { PurchaseBenefits } from '../PurchaseBenefits/PurchaseBenefits';
import { toSlug } from '../../utils/toSlug';

export const ProductDetails = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [quantity, setQuantity] = useState(1);
    const [product, setProduct] = useState(null);
    const [loading, setLoading] = useState(true);
    const [thumbnail, setThumbnail] = useState(null);
    const { user } = useAppContext(); // Lấy thông tin người dùng từ context

    useEffect(() => {
        const fetchProduct = async () => {
            try {
                const res = await axios.get(`http://localhost:4003/api/product/${id}`);
                setProduct(res.data);
            } catch (error) {
                console.error('Error fetching product:', error);
                navigate('/not-found');
            } finally {
                setLoading(false);
            }
        };
        fetchProduct();
    }, [id, navigate]);

    useEffect(() => {
        if (product) {
            setThumbnail(product?.image?.[0] || null);
        }
    }, [product]);

    const handleAddToCart = async () => {
        if (!user) {
            message.warning('Vui lòng đăng nhập để thêm sản phẩm vào giỏ hàng.');
            navigate('/login'); // Hoặc trang đăng nhập của bạn
            return;
        }

        try {
            const res = await axios.post('http://localhost:3002/api/carts', { // Đảm bảo cổng là 3002
                quantity: quantity,
                productId: product._id,
                customerId: user._id, // Sử dụng _id từ thông tin người dùng
                userId: user._id,
            });
            message.success('Đã thêm sản phẩm vào giỏ hàng!');
            // Có thể chuyển hướng người dùng đến trang giỏ hàng hoặc cập nhật trạng thái giỏ hàng
            console.log('Sản phẩm đã được thêm vào giỏ hàng:', res.data);
        } catch (error) {
            console.error('Lỗi khi thêm vào giỏ hàng:', error.message);
            message.error('Không thể thêm sản phẩm vào giỏ hàng. Vui lòng thử lại sau.');
        }
    };

    const handleIncreaseQuantity = () => {
        if (quantity < product.quantity) {
            setQuantity(quantity + 1);
        }
    };

    const handleDecreaseQuantity = () => {
        if (quantity > 1) {
            setQuantity(quantity - 1);
        }
    };

    if (loading) return <p>Đang tải...</p>;
    if (!product) return <p>Không tìm thấy sản phẩm.</p>;

    return (
        <div className="product__details">
            {/* ... (phần hiển thị thông tin sản phẩm như trước) */}
            <Col span={14}>
                <div className="product__details-info">
                    <p>{product.name}</p>
                </div>

                <div className="product___details-review">
                    <div>
                        {[...Array(5)].map((_, i) => (
                            <i className="fa-solid fa-star" key={i}></i>
                        ))}
                    </div>
                </div>

                <div className="product__details-benefit">
                    <span>An tâm mua sắm cùng Shopee</span>
                    <PurchaseBenefits />
                </div>

                <div className="product__details-price">
                    <p>đ{product.price}</p>
                </div>

                <div className="product__details-count">
                    <p>Số Lượng:</p>
                    <div className="quantity-control">
                        <button onClick={handleDecreaseQuantity}>-</button>
                        <input
                            type="number"
                            value={quantity}
                            min="1"
                            max={product.quantity}
                            onChange={(e) => {
                                const value = parseInt(e.target.value, 10);
                                if (!isNaN(value) && value >= 1 && value <= product.quantity) {
                                    setQuantity(value);
                                }
                            }}
                        />
                        <button onClick={handleIncreaseQuantity}>+</button>
                    </div>
                    <p>{product.quantity} <span>Sản phẩm {product.status}</span></p>
                </div>

                <div className="product__details-add">
                    <button className="product__details-cart" onClick={handleAddToCart}>
                        <i className="fa-solid fa-cart-shopping"></i>
                        Thêm vào giỏ hàng
                    </button>
                    <button className="product__details-voucher">
                        Mua với Voucher {product.discounted_price}
                    </button>
                </div>
            </Col>
            {/* ... (phần hiển thị hình ảnh sản phẩm như trước) */}
        </div>
    );
};
