import React from 'react';
import { useParams } from 'react-router-dom';
import { useAppContext } from '../../context/AppContext';
import categoryData from '../../assets/data/category'; // nhập danh sách category

export const ProductCategory = () => {
    const { category } = useParams(); 
    const { product } = useAppContext();

    const toSlug = (str) =>
        str.normalize("NFD")
            .replace(/[\u0300-\u036f]/g, "")
            .replace(/đ/g, "d")
            .replace(/Đ/g, "d")
            .replace(/[^a-zA-Z0-9\s]/g, "")
            .trim()
            .toLowerCase()
            .replace(/\s+/g, "-");

    const searchCategory = categoryData.find(item => toSlug(item.name) === category);
    const filteredProduct=product.filter((product)=>product.category.toSlug()===category)

    return (
        <div>
            {searchCategory && (
                <div>
                    <p>{searchCategory.name.toUpperCase()}</p>
                </div>
            )}
        </div>
    );
};
