import React, { useState } from 'react';
import './cart.css';

const mockCartItems = [
  { id: 1, name: 'Áo thun nam', price: 120000, quantity: 1, checked: false },
  { id: 2, name: 'Giày sneaker', price: 450000, quantity: 2, checked: false },
];

export const Cart = () => {
  const [cartItems, setCartItems] = useState(mockCartItems);
  const [selectAll, setSelectAll] = useState(false);

  const handleSelectAll = () => {
    const newSelectAll = !selectAll;
    setSelectAll(newSelectAll);
    setCartItems(cartItems.map(item => ({ ...item, checked: newSelectAll })));
  };

  const handleSelectItem = (id) => {
    const newCartItems = cartItems.map(item =>
      item.id === id ? { ...item, checked: !item.checked } : item
    );
    setCartItems(newCartItems);

    const allSelected = newCartItems.every(item => item.checked);
    setSelectAll(allSelected);
  };

  const handleQuantityChange = (id, type) => {
    const newCartItems = cartItems.map(item => {
      if (item.id === id) {
        let newQuantity = type === 'increase' ? item.quantity + 1 : item.quantity - 1;
        if (newQuantity < 1) newQuantity = 1;
        return { ...item, quantity: newQuantity };
      }
      return item;
    });
    setCartItems(newCartItems);
  };

  const totalPrice = cartItems
    .filter(item => item.checked)
    .reduce((sum, item) => sum + item.price * item.quantity, 0);

  return (
    <div className="cart-container">
      <h2>Giỏ hàng của bạn</h2>
      <div className="cart-header">
        <input type="checkbox" checked={selectAll} onChange={handleSelectAll} /> Chọn tất cả
      </div>
      {cartItems.map(item => (
        <div className="cart-item" key={item.id}>
          <input
            type="checkbox"
            checked={item.checked}
            onChange={() => handleSelectItem(item.id)}
          />
          <span className="item-name">{item.name}</span>
          <div className="quantity-controls">
            <button onClick={() => handleQuantityChange(item.id, 'decrease')}>-</button>
            <span>{item.quantity}</span>
            <button onClick={() => handleQuantityChange(item.id, 'increase')}>+</button>
          </div>
          <span className="item-price">{(item.price * item.quantity).toLocaleString()}₫</span>
        </div>
      ))}
      <div className="cart-footer">
        <div>Tổng tiền: <strong>{totalPrice.toLocaleString()}₫</strong></div>
        <button className="checkout-btn">Mua hàng</button>
      </div>
    </div>
  );
};
