import React, { useState, useEffect } from 'react';
import './sellerform.css';
import axios from 'axios';
import { useAppContext } from '../../context/AppContext';

export const SellerForm = () => {
    const { user } = useAppContext(); // Lấy user hiện tại
    const [storeName, setStoreName] = useState('');
    const [storeAddress, setStoreAddress] = useState('');
    const [phone, setPhone] = useState('');
    const [images, setImages] = useState([]);
    const [sellerId, setSellerId] = useState(null); // Nếu đã có seller thì lưu lại để update

    // Check nếu user đã có seller => Load dữ liệu để update
    useEffect(() => {
        const fetchSeller = async () => {
            try {
                const res = await axios.get(`http://localhost:5000/api/sellers/user/${user._id}`);
                const seller = res.data;
                setSellerId(seller._id);
                setStoreName(seller.storeName);
                setStoreAddress(seller.storeAddress);
                setPhone(seller.phone);
            } catch (err) {
                console.log('Chưa có seller cho user này');
            }
        };

        if (user && user._id) {
            fetchSeller();
        }
    }, [user]);

    const handleImageChange = (e, index) => {
        const files = [...images];
        files[index] = e.target.files[0];
        setImages(files);
    };

    const handleSubmit = async (e, type = 'create') => {
        e.preventDefault();
        const formData = new FormData();
        formData.append('storeName', storeName);
        formData.append('storeAddress', storeAddress);
        formData.append('phone', phone);
        formData.append('userId', user._id); // khi tạo

        images.forEach((img) => {
            if (img) formData.append('images', img);
        });

        try {
            if (type === 'create') {
                await axios.post('http://localhost:5000/api/sellers', formData, {
                    headers: { 'Content-Type': 'multipart/form-data' },
                });
                alert('Tạo shop thành công!');
            } else {
                await axios.put(`http://localhost:5000/api/sellers/${sellerId}`, formData, {
                    headers: { 'Content-Type': 'multipart/form-data' },
                });
                alert('Cập nhật shop thành công!');
            }
        } catch (err) {
            console.error(err);
            alert('Lỗi khi gửi dữ liệu');
        }
    };

    return (
        <div className='form__seller-info'>
            <form className="form-container-seller">
                <div className="form-group">
                    <label htmlFor="name" className="form-label">Tên Shop</label>
                    <input
                        id="name"
                        type="text"
                        className="form-input"
                        placeholder="Nhập vào đây"
                        value={storeName}
                        onChange={(e) => setStoreName(e.target.value)}
                    />
                </div>

                <div className="form-group">
                    <label htmlFor="address" className="form-label">Địa chỉ</label>
                    <input
                        id="address"
                        type="text"
                        className="form-input"
                        placeholder="Nhập vào đây"
                        value={storeAddress}
                        onChange={(e) => setStoreAddress(e.target.value)}
                    />
                </div>

                <div className="form-row">
                    <div className="form-group small">
                        <label htmlFor="phone" className="form-label">Điện thoại</label>
                        <input
                            id="phone"
                            type="text"
                            className="form-input"
                            placeholder="Nhập vào đây"
                            value={phone}
                            onChange={(e) => setPhone(e.target.value)}
                        />
                    </div>
                </div>

                <div className="form-group">
                    <label className="form-label">Avatar</label>
                    <div className="image-upload-group">
                        {Array(1).fill('').map((_, index) => (
                            <label key={index} htmlFor={`image${index}`}>
                                <input
                                    accept="image/*"
                                    type="file"
                                    id={`image${index}`}
                                    name="images"
                                    hidden
                                    onChange={(e) => handleImageChange(e, index)}
                                />
                                <img
                                    className="upload-preview"
                                    src={
                                        images[index]
                                            ? URL.createObjectURL(images[index])
                                            : "https://raw.githubusercontent.com/prebuiltui/prebuiltui/main/assets/e-commerce/uploadArea.png"
                                    }
                                    alt="upload"
                                />
                            </label>
                        ))}
                    </div>
                </div>

                <div className='btn__sellerInfo-submit'>
                    <button
                        type="button"
                        className="submit-btn-save"
                        onClick={(e) => handleSubmit(e, 'create')}
                    >
                        Lưu
                    </button>
                    <button
                        type="button"
                        className="submit-btn-update"
                        onClick={(e) => handleSubmit(e, 'update')}
                        disabled={!sellerId}
                    >
                        Cập nhật
                    </button>
                </div>
            </form>
        </div>
    );
};
