import React from 'react'
import categoryData from '../../assets/data/category'
import { Col, Row } from 'antd';
import './category.css'
import { useNavigate } from 'react-router-dom';

export const Category = () => {
    const navigate = useNavigate();
    const toSlug = (str) =>
        str.normalize("NFD") // tách dấu ra
            .replace(/[\u0300-\u036f]/g, "") // xoá dấu
            .replace(/[^a-zA-Z0-9\s]/g, "") // xoá ký tự đặc biệt
            .trim()
            .toLowerCase()
            .replace(/\s+/g, "-"); // chuyển khoảng trắng thành dấu gạch ngang


    return (
        <section className='category'>
            <h2>Danh Mục</h2>
            <Row>
                <Col span={24}>
                    <div className='category__list'>
                        {
                            categoryData.map((item) => (
                                <div
                                    key={item.id}
                                    className='category__item'
                                    onClick={() => {
                                        const path = toSlug(item.name);
                                        navigate(`/products/${item.name.toLowerCase()}`);
                                    }}
                                >
                                    <div className='category__item-img'>
                                        <img src={item.img} alt={item.name} />
                                    </div>
                                    <span>{item.name}</span>
                                </div>
                            ))
                        }
                    </div>
                </Col>
            </Row>
        </section>
    );
};
