import { createContext, useContext, useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

export const AppContext = createContext();

export const AppContextProvider = ({ children }) => {
  const [customer, setCustomer] = useState(null);
  const [seller, setSeller] = useState(null);
  const [products, setProducts] = useState([]);
  const [cartItems, setCartItems] = useState(() => {
    const storedCustomer = localStorage.getItem('customer');
    const userId = storedCustomer ? JSON.parse(storedCustomer)?._id || JSON.parse(storedCustomer)?.id : null;
    const cartKey = userId ? `cartItems_user_${userId}` : 'cartItems_guest'; // Key mặc định cho khách
    const storedCart = localStorage.getItem(cartKey);
    return storedCart ? JSON.parse(storedCart) : [];
  });
  const [categories, setCategories] = useState([]);
  const navigate = useNavigate();

  const getCartKey = () => {
    const storedCustomer = localStorage.getItem('customer');
    const userId = storedCustomer ? JSON.parse(storedCustomer)?._id || JSON.parse(storedCustomer)?.id : null;
    return userId ? `cartItems_user_${userId}` : 'cartItems_guest';
  };
  const handleLogout = () => {
    localStorage.removeItem(`cartItems_user_${customer?._id || customer?.id}`);
    localStorage.removeItem('token');
    localStorage.removeItem('customer');
    setCustomer(null);
    setSeller(null);
    setCartItems([]);
    navigate('/login');
  };
  const fetchCartItems = async (customerId) => {
    try {
      const res = await axios.get(`http://localhost:3003/api/cart/customer/${customerId}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      console.log('Cart data from server:', res.data);
      if (Array.isArray(res.data)) {
        const formattedCart = res.data.map(item => ({
          _id: item._id,
          productId: item.productId._id,
          quantity: item.quantity,
          productName: item.productId.name,
          productImage: item.productId.image?.[0],
          productPrice: item.productId.price
        }));
        setCartItems(formattedCart);
        localStorage.setItem(getCartKey(), JSON.stringify(formattedCart)); // Cập nhật Local Storage với dữ liệu từ server
      } else {
        // Nếu không có giỏ hàng trên server, vẫn giữ lại giỏ hàng từ localStorage (nếu có)
        const storedCart = localStorage.getItem(getCartKey());
        if (storedCart) {
          setCartItems(JSON.parse(storedCart));
        } else {
          setCartItems([]);
        }
      }
    } catch (error) {
      console.error("Lỗi khi tải giỏ hàng:", error);
      const storedCart = localStorage.getItem(getCartKey());
      if (storedCart) {
        setCartItems(JSON.parse(storedCart));
      } else {
        setCartItems([]);
      }
    }
  };

  const fetchCategories = async () => {
    try {
      const res = await axios.get('http://localhost:3001/api/category/all');
      setCategories(res.data || []);
    } catch (error) {
      console.error('Lỗi khi lấy danh mục:', error.message);
    }
  };

  const addToCart = async (product, quantity) => {
    try {
      const storedCustomer = JSON.parse(localStorage.getItem('customer'));
      const customerId = storedCustomer?._id || storedCustomer?.id;

      setCartItems(prev => {
        const existingItem = prev.find(item => item.productId === product._id);
        let updatedCart;
        if (existingItem) {
          updatedCart = prev.map(item =>
            item.productId === product._id
              ? { ...item, quantity: item.quantity + quantity }
              : item
          );
        } else {
          updatedCart = [
            ...prev,
            {
              productId: product._id,
              quantity,
              productName: product.name,
              productImage: product.image?.[0],
              productPrice: product.price
            }
          ];
        }
        localStorage.setItem(getCartKey(), JSON.stringify(updatedCart));
        return updatedCart;
      });

      if (customerId) {
        await axios.post('http://localhost:3003/api/cart/create', {
          productId: product._id,
          quantity,
          customerId
        }, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
      }

    } catch (error) {
      console.log(error);
    }
  };

  const fetchProduct = async () => {
    try {
      const res = await axios.get('http://localhost:4003/api/product/all');
      setProducts(res.data.products || []);
    } catch (error) {
      console.error('Lỗi khi lấy danh sách sản phẩm:', error.message);
    }
  };

  useEffect(() => {
    fetchProduct();
    fetchCategories();

    const storedCustomer = localStorage.getItem('customer');
    if (storedCustomer) {
      const customerData = JSON.parse(storedCustomer);
      setCustomer(customerData);
      if (customerData.role === 'seller') {
        setSeller(customerData);
      }
      fetchCartItems(customerData._id || customerData.id);
    } else {
      // Nếu không có thông tin khách hàng, vẫn cố gắng tải giỏ hàng (có thể là của khách)
      const storedCart = localStorage.getItem(getCartKey());
      if (storedCart) {
        setCartItems(JSON.parse(storedCart));
      }
    }
  }, []);

  useEffect(() => {
    localStorage.setItem(getCartKey(), JSON.stringify(cartItems));
  }, [cartItems]);

  const value = {
    navigate,
    products,
    cartItems,
    setCartItems,
    addToCart,
    customer,
    seller,
    setCustomer,
    setSeller,
    categories,
    handleLogout
  };

  return (
    <AppContext.Provider value={value}>
      {children}
    </AppContext.Provider>
  );
};

export const useAppContext = () => useContext(AppContext);
