import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './login.css';
import logo from '../../assets/images/logo/logo.png';

export const Login = () => {
    const navigate = useNavigate();
    const [showPassword, setShowPassword] = useState(false);
    const [emailOrPhone, setEmailOrPhone] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (token) {
            navigate('/');
        }
    }, [navigate]);

    const togglePassword = () => {
        setShowPassword(!showPassword);
    };

    const handleLogin = async (e) => {
        e.preventDefault();
        try {
            const res = await axios.post('http://localhost:3001/api/auth/login', {
                email: emailOrPhone,
                password: password
            });

            localStorage.setItem('token', res.data.token);
            localStorage.setItem('user', JSON.stringify(res.data.user));

            navigate('/');
        } catch (err) {
            setError(err.response?.data?.message || 'Đăng nhập thất bại');
        }
    };

    return (
        <div className='login'>
            <header className='login__header'>
                <div className='login__header-box'>
                    <div className='login__header-logo'>
                        <Link to="/">
                            <img src={logo} alt="Logo" />
                        </Link>
                        <h3>Đăng nhập</h3>
                    </div>
                    <a href="#">Bạn cần giúp đỡ?</a>
                </div>
            </header>

            <div className='login__container'>
                <div className='login-logoleft'>
                    <img src={logo} alt="Logo" />
                    <p>Nền tảng thương mại điện tử hàng đầu Đông Nam Á</p>
                </div>

                <form onSubmit={handleLogin}>
                    <span>Đăng nhập</span>
                    {error && <p style={{ color: 'red', textAlign: 'center' }}>{error}</p>}

                    <div className='form-group'>
                        <input
                            type="text"
                            placeholder="Số điện thoại/Email/Tên đăng nhập"
                            value={emailOrPhone}
                            onChange={(e) => setEmailOrPhone(e.target.value)}
                        />
                        <div className="password-input-wrapper">
                            <input
                                type={showPassword ? "text" : "password"}
                                placeholder="Mật khẩu"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                            />
                            <span className="toggle-password" onClick={togglePassword}>
                                <i className={`fa-solid ${showPassword ? "fa-eye-slash" : "fa-eye"}`}></i>
                            </span>
                        </div>
                    </div>

                    <button type="submit" className='btn-submit'>Đăng nhập</button>

                    <div className='login__options'>
                        <span>Quên mật khẩu</span>
                        <span>Đăng nhập với SMS</span>
                    </div>

                    <div className="social-login">
                        <button className="btn-facebook">
                            <i className="fa-brands fa-facebook"></i>Facebook
                        </button>
                        <button className="btn-google">
                            <i className="fa-brands fa-google"></i>Google
                        </button>
                    </div>

                    <p className="no-account">
                        Bạn mới biết đến Shopee? <Link to="/register">Đăng ký</Link>
                    </p>
                </form>
            </div>
        </div>
    );
};
