import React, { useEffect, useState } from 'react';
import './productdetails.css';
import { useAppContext } from '../../context/AppContext';
import { useParams, Link } from 'react-router-dom';
import { Button, Col, Row } from 'antd';

export const ProductDetails = () => {
  const { id } = useParams();
  const { product, navigate } = useAppContext();
  const [thumbnail, setThumbnail] = useState(null);
  const [relatedProducts, setRelatedProducts] = useState([]);

  const currentProduct = product.find((item) => item._id === id);

  useEffect(() => {
    if (currentProduct && product.length > 0) {
      const productsCopy = product.filter(
        (item) => item.category === currentProduct.category && item._id !== id
      );
      setRelatedProducts(productsCopy.slice(0, 5)); 
    }
  }, [product, currentProduct, id]);

  useEffect(() => {
    if (currentProduct?.image && currentProduct.image.length > 1) {
      setThumbnail(currentProduct.image[1]);
    }
  }, [currentProduct]);

  if (!currentProduct) {
    return <div>Loading...</div>;
  }

  return (
    <>
      <Row>
        <div className="breadcrumb">
          <ul>
            <li><Link to="/">Home</Link></li>
            <li><Link to={"/product"}>Products</Link></li>
          </ul>
        </div>
      </Row>
      <Row className="product-details">
        <Col span={12}>
          <img src={thumbnail || currentProduct.image[0]} alt={currentProduct.name} />
        </Col>
        <Col span={12}>
          <h2>{currentProduct.name}</h2>
          <p>{currentProduct.description}</p>
          <p>Price: ${currentProduct.price}</p>
          <Button type="primary">Add to Cart</Button>
        </Col>
      </Row>
    </>
  );
};
