import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './cart.css';

export const Cart = () => {
  const navigate = useNavigate();
  const [cartItems, setCartItems] = useState([]);
  const [selectedItems, setSelectedItems] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);

  useEffect(() => {
    // Giả sử chúng ta đã có một API để lấy giỏ hàng của người dùng
    const fetchCart = async () => {
      try {
        const userId = localStorage.getItem('userId'); // Lấy thông tin user từ localStorage
        const response = await axios.get(`http://localhost:3003/api/cart/user/${userId}`);
        setCartItems(response.data);
      } catch (error) {
        console.error('Lỗi khi lấy giỏ hàng:', error);
      }
    };
    fetchCart();
  }, []);

  useEffect(() => {
    // Tính toán tổng giá trị khi giỏ hàng hoặc sản phẩm được chọn thay đổi
    const total = cartItems
      .filter(item => selectedItems.includes(item.productId))
      .reduce((sum, item) => sum + item.productPrice * item.quantity, 0);
    setTotalPrice(total);
  }, [cartItems, selectedItems]);

  const handleSelectItem = (productId) => {
    if (selectedItems.includes(productId)) {
      setSelectedItems(selectedItems.filter(id => id !== productId));
    } else {
      setSelectedItems([...selectedItems, productId]);
    }
  };

  const handleSelectAll = () => {
    if (selectedItems.length === cartItems.length) {
      setSelectedItems([]);
    } else {
      setSelectedItems(cartItems.map(item => item.productId));
    }
  };

  const handleQuantityChange = (productId, action) => {
    setCartItems(cartItems.map(item => {
      if (item.productId === productId) {
        if (action === 'increase') {
          item.quantity += 1;
        } else if (action === 'decrease' && item.quantity > 1) {
          item.quantity -= 1;
        }
      }
      return item;
    }));
  };

  const handleDeleteItem = (productId) => {
    setCartItems(cartItems.filter(item => item.productId !== productId));
  };

  const handleCheckout = () => {
    // Điều hướng đến trang thanh toán hoặc xử lý giỏ hàng
    navigate('/checkout');
  };

  return (
    <div className="cart">
      <h2>Giỏ hàng của bạn</h2>

      {cartItems.length === 0 ? (
        <div className="empty-cart">
          <p>Giỏ hàng trống</p>
          <Link to="/" className="btn-continue">Tiếp tục mua sắm</Link>
        </div>
      ) : (
        <>
          <div className="cart-header">
            <input
              type="checkbox"
              checked={selectedItems.length === cartItems.length}
              onChange={handleSelectAll}
            />
            <span>Chọn tất cả</span>
          </div>

          <div className="cart-items">
            {cartItems.map((item) => (
              <div className="cart-item" key={item.productId}>
                <input
                  type="checkbox"
                  checked={selectedItems.includes(item.productId)}
                  onChange={() => handleSelectItem(item.productId)}
                />
                <img src={item.productImage} alt={item.productName} />
                <div className="cart-item-info">
                  <h4>{item.productName}</h4>
                  <p>Giá: đ{item.productPrice.toLocaleString()}</p>

                  <div className="quantity-control">
                    <button onClick={() => handleQuantityChange(item.productId, 'decrease')}>-</button>
                    <input type="text" value={item.quantity} readOnly />
                    <button onClick={() => handleQuantityChange(item.productId, 'increase')}>+</button>
                  </div>

                  <p>Thành tiền: đ{(item.productPrice * item.quantity).toLocaleString()}</p>

                  <button className="btn-delete" onClick={() => handleDeleteItem(item.productId)}>
                    Xóa
                  </button>
                </div>
              </div>
            ))}
          </div>

          <div className="cart-footer">
            <div className="footer-left">
              <input
                type="checkbox"
                checked={selectedItems.length === cartItems.length}
                onChange={handleSelectAll}
              />
              <span>Chọn tất cả ({selectedItems.length})</span>
            </div>

            <div className="footer-right">
              <p>
                Tổng thanh toán: <span>đ{totalPrice.toLocaleString()}</span>
              </p>
              <button className="btn-buy" onClick={handleCheckout}>Mua hàng</button>
            </div>
          </div>
        </>
      )}
    </div>
  );
};
