import React, { useState, useEffect } from 'react';
import './addProducts.css';
import axios from 'axios';
import { useAppContext } from '../../context/AppContext';
import { useNavigate } from 'react-router-dom';

export const AddProducts = () => {
    const { categories, user } = useAppContext();
    const navigate = useNavigate();
    const sellerId = localStorage.getItem('sellerId');
    const token = localStorage.getItem('token');

    const [images, setImages] = useState([]);
    const [name, setName] = useState('');
    const [description, setDescription] = useState('');
    const [idCategory, setIdCategory] = useState('');
    const [status, setStatus] = useState('in_stock');
    const [price, setPrice] = useState('');
    const [quantity, setQuantity] = useState('');
    const [isSubmitting, setIsSubmitting] = useState(false);
    const [uploadingImages, setUploadingImages] = useState(false);
    const [imageUrls, setImageUrls] = useState([]);

    const handleImageChange = (e, index) => {
        const newImages = [...images];
        newImages[index] = e.target.files[0];
        setImages(newImages);
    };

    const handleInputChange = (e, setter) => {
        setter(e.target.value);
    };

    const handleCategoryChange = (e) => {
        setIdCategory(e.target.value);
    };

    const handleStatusChange = (e) => {
        setStatus(e.target.value);
    };

    const uploadImage = async (file) => {
        const formData = new FormData();
        formData.append('image', file);
        try {
            const res = await axios.post('http://localhost:3006/api/upload', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                    'Authorization': `Bearer ${token}`,
                },
            });
            return res.data.url;
        } catch (error) {
            console.error('Lỗi tải ảnh lên:', error);
            return null;
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!sellerId) {
            alert('Bạn cần tạo shop trước khi thêm sản phẩm.');
            return;
        }

        if (!name || !description || !idCategory || !price || !quantity || images.length === 0) {
            alert('Vui lòng điền đầy đủ thông tin và chọn ít nhất một hình ảnh.');
            return;
        }

        setUploadingImages(true);
        const uploadedUrls = await Promise.all(images.filter(img => img).map(uploadImage));
        setUploadingImages(false);

        if (uploadedUrls.some(url => !url)) {
            alert('Có lỗi xảy ra khi tải ảnh lên. Vui lòng thử lại.');
            return;
        }

        const newProduct = {
            name,
            describe: description,
            idCategory,
            price: parseFloat(price),
            quantity: parseInt(quantity),
            status,
            image: uploadedUrls, // Sử dụng mảng URLs tải lên
            idSeller: sellerId, // Liên kết sản phẩm với người bán
            idUser: user?.id, // Thêm idUser nếu cần thiết
        };

        try {
            setIsSubmitting(true);
            const res = await axios.post('http://localhost:4003/api/product/create', newProduct, {
                headers: {
                    'Authorization': `Bearer ${token}`,
                },
            });
            alert('Thêm sản phẩm thành công!');
            navigate('/seller/products'); // Chuyển hướng sau khi thêm thành công
            // Reset form
            setName('');
            setDescription('');
            setIdCategory('');
            setStatus('in_stock');
            setPrice('');
            setQuantity('');
            setImages([]);
            setImageUrls([]);
        } catch (error) {
            console.error('Lỗi khi thêm sản phẩm:', error.response?.data || error.message);
            alert('Có lỗi xảy ra khi thêm sản phẩm.');
        } finally {
            setIsSubmitting(false);
        }
    };

    return (
        <div className="product-form">
            <form className="form-container" onSubmit={handleSubmit}>
                <div className="form-group">
                    <label className="form-label">Hình ảnh sản phẩm</label>
                    <div className="image-upload-group">
                        {Array(6).fill('').map((_, index) => (
                            <label key={index} htmlFor={`image${index}`}>
                                <input
                                    accept="image/*"
                                    type="file"
                                    id={`image${index}`}
                                    name="images"
                                    hidden
                                    onChange={(e) => handleImageChange(e, index)}
                                />
                                <img
                                    className="upload-preview"
                                    src={images[index] ? URL.createObjectURL(images[index]) : "https://raw.githubusercontent.com/prebuiltui/prebuiltui/main/assets/e-commerce/uploadArea.png"}
                                    alt="upload"
                                />
                            </label>
                        ))}
                        {uploadingImages && <p>Đang tải ảnh lên...</p>}
                    </div>
                </div>

                <div className="form-group">
                    <label htmlFor="name" className="form-label">Tên sản phẩm</label>
                    <input
                        id="name"
                        type="text"
                        className="form-input"
                        placeholder="Nhập vào đây"
                        value={name}
                        onChange={(e) => handleInputChange(e, setName)}
                        required
                    />
                </div>

                <div className="form-group">
                    <label htmlFor="description" className="form-label">Mô tả sản phẩm</label>
                    <textarea
                        id="description"
                        className="form-input"
                        rows="4"
                        placeholder="Nhập vào đây"
                        value={description}
                        onChange={(e) => handleInputChange(e, setDescription)}
                    ></textarea>
                </div>

                <div className="form-group">
                    <label htmlFor="category" className="form-label">Danh mục</label>
                    <select
                        id="category"
                        className="form-input"
                        value={idCategory}
                        onChange={handleCategoryChange}
                        required
                    >
                        <option value="">Chọn danh mục</option>
                        {categories.map(cat => (
                            <option key={cat._id} value={cat._id}>{cat.name}</option>
                        ))}
                    </select>

                    <label htmlFor="status" className="form-label">Trạng thái</label>
                    <select
                        id="status"
                        className="form-input"
                        value={status}
                        onChange={handleStatusChange}
                    >
                        <option value="in_stock">Còn hàng</option>
                        <option value="out_of_stock">Hết hàng</option>
                    </select>
                </div>

                <div className="form-row">
                    <div className="form-group small">
                        <label htmlFor="price" className="form-label">Giá sản phẩm</label>
                        <input
                            id="price"
                            type="number"
                            className="form-input"
                            placeholder="0"
                            value={price}
                            onChange={(e) => handleInputChange(e, setPrice)}
                            required
                        />
                    </div>
                    <div className="form-group small">
                        <label htmlFor="quantity" className="form-label">Số lượng</label>
                        <input
                            id="quantity"
                            type="number"
                            className="form-input"
                            placeholder="0"
                            value={quantity}
                            onChange={(e) => handleInputChange(e, setQuantity)}
                            required
                        />
                    </div>
                </div>

                <button type="submit" className="submit-btn" disabled={isSubmitting || uploadingImages}>
                    {isSubmitting ? 'Đang thêm...' : 'THÊM SẢN PHẨM'}
                </button>
            </form>
        </div>
    );
};
