import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './productrelated.css';

export const ProductRelated = ({ productId }) => {
  const [relatedProducts, setRelatedProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchRelatedProducts = async () => {
      try {
        const res = axios.get(`http://localhost:4003/api/products/${productId}/related`);
        setRelatedProducts(res.data);
      } catch (error) {
        console.error('Lỗi khi lấy sản phẩm liên quan:', error);
      } finally {
        setLoading(false);
      }
    };

    if (productId) {
      fetchRelatedProducts();
    }
  }, [productId]);

  if (loading) return <div>Đang tải sản phẩm liên quan...</div>;

  if (relatedProducts.length === 0) return <div>Không có sản phẩm liên quan.</div>;

  return (
    <div className="related-products">
      <h3>Sản phẩm cùng danh mục</h3>
      <div className="related-list">
        {relatedProducts.map(product => (
          <div key={product._id} className="related-item">
            <img src={product.image[0]} alt={product.name} />
            <h4>{product.name}</h4>
            <p>Giá: {product.price.toLocaleString()} VND</p>
          </div>
        ))}
      </div>
    </div>
  );
};
