import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './shoppages.css';
import { useParams } from 'react-router-dom';

export const ShopPages = () => {
  const { sellerId } = useParams();
  const [shopInfo, setShopInfo] = useState(null);
  const [categories, setCategories] = useState([]);

  // Gọi API lấy thông tin seller
  const fetchShopInfo = async () => {
    try {
      const res = await axios.get(`http://localhost:4003/api/seller/${sellerId}`);
      setShopInfo({
        name: res.data.storeName,
        avatar: res.data.image || '/default-avatar.png',
        address: res.data.storeAddress,
        phone: res.data.phone,
      });
    } catch (error) {
      console.error('Lỗi khi lấy thông tin seller:', error);
    }
  };

  // Gọi API lấy sản phẩm và lọc ra danh mục duy nhất
  const fetchSellerCategories = async () => {
    try {
      const res = await axios.get(`http://localhost:4003/api/product/sellers/${sellerId}`);
      const uniqueCategories = [
        ...new Set(res.data.map((product) => product.categoryId?.name).filter(Boolean)),
      ];
      setCategories(uniqueCategories);
    } catch (error) {
      console.error('Lỗi khi lấy danh mục sản phẩm:', error);
    }
  };

  useEffect(() => {
    if (sellerId) {
      fetchShopInfo();
      fetchSellerCategories();
    }
  }, [sellerId]);

  if (!shopInfo) return <div>Đang tải thông tin shop...</div>;

  return (
    <div className="shop-page">
      <div className="shopIno">
        <div className="shop-info-left">
          <img className="shop-avatar" src={shopInfo.avatar} alt="Shop Avatar" />
          <div className="shop-details">
            <h2 className="shop-name">{shopInfo.name}</h2>
            <p className="shop-online">Online: 7 phút trước</p>
            <p className="shop-address">📍 {shopInfo.address}</p>
            <p className="shop-phone">📞 {shopInfo.phone}</p>
          </div>
        </div>
      </div>

      <div className="shop__category">
        <h3>Danh mục sản phẩm:</h3>
        <ul>
          {categories.map((cat, index) => (
            <li key={index}>🔹 {cat}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};
