import React, { useState, useEffect } from 'react';
import { useAppContext } from '../../context/AppContext';
import axios from 'axios';
import './order.css';

export const Order = () => {
    const { cartItems, products } = useAppContext();
    const [paymentMethods, setPaymentMethods] = useState([]);
    const [selectedPaymentMethod, setSelectedPaymentMethod] = useState('cod');

    const [formData, setFormData] = useState({
        name: '',
        email: '',
        phone: '',
        city: '',
        describe: ''
    });

    const handleChange = (e) => {
        setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
    };

    const handlePaymentChange = (e) => {
        setSelectedPaymentMethod(e.target.value);
    };

    useEffect(() => {
        const fetchPaymentMethods = async () => {
            try {
                const response = await axios.get('http://localhost:3008/api/paymentMethod/all');
                setPaymentMethods(response.data);
            } catch (error) {
                console.error('Error fetching payment methods:', error);
            }
        };

        fetchPaymentMethods();
    }, []);

    const selectedItems = cartItems.filter(item => item.selected);

    const getProductById = (id) => products.find((p) => p._id === id);

    const totalPrice = selectedItems.reduce((acc, item) => {
        const prod = getProductById(item.productId);
        if (!prod) return acc;
        const price = prod.discounted_price || prod.price || 0;
        return acc + price * item.quantity;
    }, 0);

    const handleSubmit = async () => {
        try {
            // 1. Tạo customer
            const customerRes = await axios.post('http://localhost:3004/api/customers/create', {
                name: formData.name,
                email: formData.email,
                phone: formData.phone
            });
    
            const customerId = customerRes.data._id;
    
            // 2. Tạo địa chỉ
            await axios.post('http://localhost:3008/api/address/create', {
                city: formData.city,
                describe: formData.describe,
                customerId: customerId
            });
    
            // 3. Tạo payment
            await axios.post('http://localhost:3007/api/payment/create', {
                amount: totalPrice,
                paymentMethodId: selectedPaymentMethod,
                paymentStatusId: "68175d9f4173f867fcba445b" 
            });
    
            alert('Đặt hàng thành công!');
        } catch (error) {
            console.error('Lỗi khi đặt hàng:', error);
            alert('Đã có lỗi xảy ra khi đặt hàng.');
        }
    };
    
    return (
        <div className="payment-container">
            <div className="address-section">
                <h3>Địa chỉ nhận hàng</h3>
                <form className="address-form" onSubmit={(e) => { e.preventDefault(); handleSubmit(); }}>
                    <input name="name" type="text" placeholder="Họ và tên" value={formData.name} onChange={handleChange} required />
                    <input name="email" type="email" placeholder="Email" value={formData.email} onChange={handleChange} required />
                    <input name="phone" type="text" placeholder="Phone" value={formData.phone} onChange={handleChange} required />
                    <input name="city" type="text" placeholder="Tỉnh / Thành phố" value={formData.city} onChange={handleChange} required />
                    <input name="describe" type="text" placeholder="Địa chỉ chi tiết" value={formData.describe} onChange={handleChange} required />
                </form>
            </div>

            <div className="payment-method-section">
                <h3>Phương thức thanh toán</h3>
                <div className="payment-options">
                    {paymentMethods.map((method) => (
                        <label key={method._id}>
                            <input
                                type="radio"
                                name="paymentMethod"
                                value={method._id}
                                checked={selectedPaymentMethod === method._id}
                                onChange={handlePaymentChange}
                            />
                            {method.namePaymentMethod} 
                        </label>
                    ))}
                </div>
            </div>

            <div className="product-summary">
                <h3>Sản phẩm</h3>
                {selectedItems.map((item, index) => {
                    const prod = getProductById(item.productId);
                    if (!prod) return null;

                    const price = prod.discounted_price || prod.price || 0;
                    return (
                        <div className="summary-item" key={index}>
                            <img
                                src={prod.img?.[0] || prod.image?.[0] || 'fallback.jpg'}
                                alt={prod.name}
                            />
                            <div>
                                <p>{prod.name}</p>
                                <p>Số lượng: {item.quantity}</p>
                                <p>Giá: đ{(price * item.quantity).toLocaleString('vi-VN')}</p>
                            </div>
                        </div>
                    );
                })}
            </div>

            <div className="payment-summary">
                <h3>Tổng thanh toán</h3>
                <p>đ{totalPrice.toLocaleString('vi-VN')}</p>
                <button className="btn-confirm" onClick={handleSubmit}>Đặt hàng</button>
            </div>
        </div>
    );
};
