import React from 'react'

export const Banner = ({ images = [], children }) => {
    const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1,
        autoplay: true,
        autoplaySpeed: 2000,
        responsive: [
            {
                breakpoint: 1024,
                settings: {
                    slidesToShow: 2,
                }
            },
            {
                breakpoint: 600,
                settings: {
                    slidesToShow: 1,
                }
            }
        ]
    };
  return (
    <section className="relative">
    <Slider {...settings}>
      {images.map((src, index) => (
        <div key={index}>
          <img src={src} alt={`banner-${index}`} className="w-full h-auto object-cover" />
        </div>
      ))}
    </Slider>
    {/* Optional children */}
    <div className="absolute top-0 left-0 w-full h-full flex items-center justify-center z-10">
      {children}
    </div>
  </section>
  )
}
