import React, { useState, useEffect } from 'react';
import './sellerform.css';
import axios from 'axios';
import { useAppContext } from '../../context/AppContext';

export const SellerForm = () => {
    const { user } = useAppContext(); // Lấy userId từ context
    const [images, setImages] = useState([]); // Lưu trữ hình ảnh
    const [storeName, setStoreName] = useState(''); // Tên shop
    const [storeAddress, setStoreAddress] = useState(''); // Địa chỉ shop
    const [phone, setPhone] = useState(''); // Số điện thoại
    const [message, setMessage] = useState(''); // Trạng thái thông báo

    const handleImageChange = (e, index) => {
        const files = [...images];
        files[index] = e.target.files[0];
        setImages(files);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        console.log("Submitting form...");

        const formData = new FormData();
        formData.append('storeName', storeName);
        formData.append('storeAddress', storeAddress);
        formData.append('phone', phone);
        formData.append('userId', user._id); // Thêm userId vào dữ liệu gửi đi

        // Thêm ảnh vào formData
        images.forEach((image) => {
            if (image) formData.append('image', image);
        });

        try {
            console.log("Sending data to API...");
            const res = await axios.post('http://localhost:3006/api/seller/create', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });

            console.log("Response from API:", res.data); // Log dữ liệu trả về từ API
            setMessage('Tạo Seller thành công!');
            console.log('Message after success:', 'Tạo Seller thành công!');
        } catch (err) {
            console.error('Lỗi khi gửi dữ liệu:', err.response?.data || err.message);
            const errorMessage = err.response?.data?.message || 'Có lỗi xảy ra. Vui lòng thử lại!';
            setMessage(errorMessage);
            console.log('Message after error:', errorMessage); // Log thông báo lỗi
        }
    };

    // Sử dụng useEffect để log mỗi khi `message` thay đổi
    useEffect(() => {
        console.log('Message updated:', message);
    }, [message]);

    return (
        <div className='form__seller-info'>
            <form className="form-container-seller" onSubmit={handleSubmit}>
                <div className="form-group">
                    <label className="form-label">Tên Shop</label>
                    <input
                        type="text"
                        className="form-input"
                        value={storeName}
                        onChange={(e) => setStoreName(e.target.value)}
                        required
                    />
                </div>

                <div className="form-group">
                    <label className="form-label">Địa chỉ</label>
                    <input
                        type="text"
                        className="form-input"
                        value={storeAddress}
                        onChange={(e) => setStoreAddress(e.target.value)}
                        required
                    />
                </div>

                <div className="form-group small">
                    <label className="form-label">Điện thoại</label>
                    <input
                        type="text"
                        className="form-input"
                        value={phone}
                        onChange={(e) => setPhone(e.target.value)}
                        required
                    />
                </div>

                <div className="form-group">
                    <label className="form-label">Avatar</label>
                    <div className="image-upload-group">
                        {[...Array(1)].map((_, index) => (
                            <label key={index} htmlFor={`image${index}`}>
                                <input
                                    accept="image/*"
                                    type="file"
                                    id={`image${index}`}
                                    name="images"
                                    hidden
                                    onChange={(e) => handleImageChange(e, index)}
                                />
                                <img
                                    className="upload-preview"
                                    src={images[index] ? URL.createObjectURL(images[index]) : "https://raw.githubusercontent.com/prebuiltui/prebuiltui/main/assets/e-commerce/uploadArea.png"}
                                    alt="upload"
                                />
                            </label>
                        ))}
                    </div>
                </div>

                <button type="submit" className="submit-btn-save">Lưu</button>

                {/* Hiển thị thông báo sau khi gửi dữ liệu */}
                {console.log('Rendering message:', message)}
                {message && <div className="message">{message}</div>}
            </form>
        </div>
    );
};
