import React, { useEffect, useState } from 'react';
import './productdetails.css';
import { useAppContext } from '../../context/AppContext';
import { useParams, Link } from 'react-router-dom';
import { Button, Col, Row } from 'antd';
import { toSlug } from '../../utils/toSlug';

export const ProductDetails = () => {
  const { id } = useParams();
  const { product, navigate } = useAppContext();
  const [thumbnail, setThumbnail] = useState(null);
  const [relatedProducts, setRelatedProducts] = useState([]);

  // Kiểm tra sản phẩm có trong mảng hay không và lấy sản phẩm cần tìm
  const products = product ? product.find((item) => item.id === Number(id)) : null;

  useEffect(() => {
    if (products) {
      // Lọc sản phẩm theo cùng category
      let productsCopy = product.filter((item) => item.category === products.category);
      setRelatedProducts(productsCopy.slice(0, 5)); // Lấy 5 sản phẩm liên quan
    }
  }, [product, products]);

  useEffect(() => {
    // Cập nhật thumbnail nếu có sản phẩm
    if (products && products.img && products.img.length > 0) {
      setThumbnail(products.img[0]);
    }
  }, [products]);

  if (!products) {
    return <p>Không tìm thấy sản phẩm</p>;
  }

  return (
    <>
      <ul>
        <li><Link to="/">Home</Link></li>
        <li><Link to="/products">Products</Link></li>
        <li><Link>{products.name}</Link></li>
      </ul>

      <div className="product-detail">
        <Row>
          <Col span={12}>
            <img src={thumbnail} alt={products.name} />
          </Col>
          <Col span={12}>
            <h2>{products.name}</h2>
            <p>{products.price}</p>
            <Button>Mua ngay</Button>
          </Col>
        </Row>
      </div>

      {/* Hiển thị các sản phẩm liên quan nếu có */}
      <div>
        <h3>Sản phẩm liên quan</h3>
        <div className="related-products">
          {relatedProducts.map((item) => (
            <div key={item.id}>
              <img src={item.img[0]} alt={item.name} />
              <p>{item.name}</p>
            </div>
          ))}
        </div>
      </div>
    </>
  );
};
