import React, { useState, useEffect } from 'react';
import { useAppContext } from '../../context/AppContext';
import { Link } from 'react-router-dom';
import './cart.css';

export const Cart = () => {
  const { cartItems, product, updateCartItem, removeCartItem } = useAppContext();
  const [selectAll, setSelectAll] = useState(false);
  const [selectedItems, setSelectedItems] = useState([]);

  useEffect(() => {
    if (cartItems.length > 0) {
      setSelectedItems(cartItems.map(item => item.productId));
    }
  }, [cartItems]);

  const handleSelectAll = () => {
    if (selectAll) {
      setSelectedItems([]);
    } else {
      setSelectedItems(cartItems.map(item => item.productId));
    }
    setSelectAll(!selectAll);
  };

  const handleSelectItem = (productId) => {
    if (selectedItems.includes(productId)) {
      setSelectedItems(selectedItems.filter(id => id !== productId));
    } else {
      setSelectedItems([...selectedItems, productId]);
    }
  };

  const handleQuantityChange = (productId, type) => {
    updateCartItem(productId, type);
  };

  const handleRemoveItem = (productId) => {
    removeCartItem(productId);
  };

  const totalPrice = cartItems
    .filter(item => selectedItems.includes(item.productId))
    .reduce((sum, item) => {
      const prod = product.find(p => p.id === item.productId);
      return prod ? sum + prod.discounted_price * item.quantity : sum;
    }, 0);

  if (cartItems.length === 0) {
    return (
      <div className="cart-empty">
        <h2>Giỏ hàng của bạn đang trống!</h2>
        <Link to="/product" className="go-shopping">Mua sắm ngay</Link>
      </div>
    );
  }

  return (
    <div className="cart-container">
      <h2>Giỏ Hàng</h2>
      <div className="cart-header">
        <input type="checkbox" checked={selectAll} onChange={handleSelectAll} /> Chọn tất cả
      </div>

      {cartItems.map(item => {
        const prod = product.find(p => p.id === item.productId);
        if (!prod) return null;
        return (
          <div className="cart-item" key={item.productId}>
            <input
              type="checkbox"
              checked={selectedItems.includes(item.productId)}
              onChange={() => handleSelectItem(item.productId)}
            />
            <img src={prod.img[0]} alt={prod.name} className="cart-item-img" />
            <div className="cart-item-info">
              <p className="cart-item-name">{prod.name}</p>
              <p className="cart-item-price">đ{prod.discounted_price.toLocaleString()}</p>
              <div className="cart-item-quantity">
                <button onClick={() => handleQuantityChange(item.productId, 'decrease')}>-</button>
                <span>{item.quantity}</span>
                <button onClick={() => handleQuantityChange(item.productId, 'increase')}>+</button>
              </div>
              <button className="cart-item-remove" onClick={() => handleRemoveItem(item.productId)}>
                Xóa
              </button>
            </div>
          </div>
        );
      })}

      <div className="cart-footer">
        <div>
          Tổng tiền: <strong>đ{totalPrice.toLocaleString()}</strong>
        </div>
        <button className="checkout-btn">Mua hàng</button>
      </div>
    </div>
  );
};
