import React, { useState, useEffect } from 'react';
import { useAppContext } from '../../context/AppContext';
import axios from 'axios';
import './order.css';

export const Order = () => {
  const { user } = useAppContext();
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedTransportId, setSelectedTransportId] = useState('');
  const [shippingInfo, setShippingInfo] = useState({
    name: '',
    phone: '',
    province: '',
    address: ''
  });

  useEffect(() => {
    const fetchCart = async () => {
      try {
        const res = await axios.get(`http://localhost:3003/api/cart/user/${user.id}`);
        setCartItems(res.data);
      } catch (error) {
        console.error('Lỗi khi lấy giỏ hàng:', error);
      } finally {
        setLoading(false);
      }
    };

    if (user?.id) {
      fetchCart();
    }
  }, [user]);

  const handleTransportChange = (e) => {
    setSelectedTransportId(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const orderData = {
        totalOrder: cartItems.reduce(
          (total, item) => total + item.quantity * item.productId.price, 
          0
        ),
        shippingInfo,
        transportId: selectedTransportId,
        customerId: user.id,
        paymentId: 'paymentId',  // Replace with actual payment ID
        userId: user.id,
        status: 'pending', // Set status as pending
      };

      const response = await axios.post('http://localhost:3003/api/orders/create', orderData);

      if (response.status === 201) {
        alert('Đặt hàng thành công');
        // Redirect or perform any other actions after order is placed
      }
    } catch (error) {
      console.error('Lỗi khi đặt hàng:', error);
    }
  };

  return (
    <div className="payment-container">
      <div className="address-section">
        <h3>Địa chỉ nhận hàng</h3>
        <form className="address-form">
          <input 
            type="text" 
            placeholder="Họ và tên" 
            name="name" 
            required 
            onChange={(e) => setShippingInfo({...shippingInfo, name: e.target.value})} 
          />
          <input 
            type="number" 
            placeholder="Phone" 
            name="phone" 
            required 
            onChange={(e) => setShippingInfo({...shippingInfo, phone: e.target.value})}
          />
          <input 
            type="text" 
            placeholder="Tỉnh / Thành phố" 
            name="province" 
            required 
            onChange={(e) => setShippingInfo({...shippingInfo, province: e.target.value})} 
          />
          <input 
            type="text" 
            placeholder="Địa chỉ chi tiết" 
            name="address" 
            required 
            onChange={(e) => setShippingInfo({...shippingInfo, address: e.target.value})} 
          />
        </form>
      </div>

      <div className="payment-method-section">
        <h3>Phương thức thanh toán</h3>
        <label>
          <input type="radio" name="paymentMethod" value="cod" /> Thanh toán khi nhận hàng (COD)
        </label>
      </div>

      <div className="transport-method">
        <h3>Phương thức vận chuyển</h3>
        <label>
          <input 
            type="radio" 
            name="transportMethod" 
            value="transportId1" 
            onChange={handleTransportChange} 
          /> GHN: 35000 Đ
        </label>
        <label>
          <input 
            type="radio" 
            name="transportMethod" 
            value="transportId2" 
            onChange={handleTransportChange} 
          /> GHTK: 30000 Đ
        </label>
        <label>
          <input 
            type="radio" 
            name="transportMethod" 
            value="transportId3" 
            onChange={handleTransportChange} 
          /> Viettel Post: 25000 Đ
        </label>
      </div>

      <div className="product-status">
        <h3>Trạng thái</h3>
        <span>Pending</span>
      </div>

      <div className="product-summary">
        <h3>Sản phẩm</h3>
        {loading ? (
          <p>Đang tải...</p>
        ) : cartItems.length === 0 ? (
          <p>Giỏ hàng trống</p>
        ) : (
          cartItems.map((item) => (
            <div className="summary-item" key={item._id}>
              <img src={item.productId.image[0]} alt={item.productId.name} />
              <div>
                <p>{item.productId.name}</p>
                <p>Số lượng: {item.quantity}</p>
                <p>Giá: {item.productId.price.toLocaleString()} đ</p>
              </div>
            </div>
          ))
        )}
      </div>

      <div className="payment-summary">
        <h3>Tổng thanh toán</h3>
        <p>
          {cartItems
            .reduce((total, item) => total + item.quantity * item.productId.price, 0)
            .toLocaleString()} đ
        </p>
        <button className="btn-confirm" onClick={handleSubmit}>Đặt hàng</button>
      </div>
    </div>
  );
};
