import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './order.css';

export const Order = () => {
  const navigate = useNavigate();
  const [selectedItems, setSelectedItems] = useState([]);
  const [userData, setUserData] = useState(null);
  const [address, setAddress] = useState({
    name: '',
    phone: '',
    province: '',
    address: ''
  });
  const [paymentMethod, setPaymentMethod] = useState('');
  const [totalPrice, setTotalPrice] = useState(0);

  useEffect(() => {
    const user = localStorage.getItem('user');
    setUserData(user ? JSON.parse(user) : null);
    // Lấy dữ liệu giỏ hàng
    const fetchCart = async () => {
      if (userData) {
        try {
          const cartRes = await axios.get(`http://localhost:3003/api/cart/user/${userData.id}`);
          const enrichedCart = cartRes.data.map(item => ({
            ...item,
            totalPrice: item.product?.price * item.quantity
          }));
          setSelectedItems(enrichedCart);
          setTotalPrice(enrichedCart.reduce((total, item) => total + item.totalPrice, 0));
        } catch (error) {
          console.error('Error fetching cart:', error);
        }
      }
    };
    fetchCart();
  }, [userData]);

  const handlePlaceOrder = async () => {
    if (!userData || !selectedItems.length || !address || !paymentMethod) {
      alert('Vui lòng hoàn thành thông tin!');
      return;
    }
    try {
      const orderData = {
        totalOrder: totalPrice,
        shippingInfo: address,
        discountId: [],  // Có thể thêm mã giảm giá nếu cần
        customerId: userData.id,
        paymentId: paymentMethod,
        transportId: 'some_transport_id',  // Cần phải lấy transport thông qua API hoặc thông tin mặc định
        userId: userData.id
      };

      // Tạo đơn hàng mới
      const orderRes = await axios.post('http://localhost:3001/api/order', orderData);
      const orderId = orderRes.data._id;

      // Tạo chi tiết đơn hàng (Order Details)
      await Promise.all(
        selectedItems.map(item =>
          axios.post('http://localhost:3002/api/orderDetails', {
            orderId,
            productId: item.productId._id,
            paymentId: paymentMethod,
            quantity: item.quantity,
            totalPrice: item.totalPrice
          })
        )
      );

      alert('Đặt hàng thành công!');
      navigate('/order-success'); // Chuyển đến trang thành công hoặc tương tự
    } catch (err) {
      console.error('Error placing order:', err);
      alert('Đặt hàng không thành công, vui lòng thử lại!');
    }
  };

  return (
    <div className="payment-container">
      <div className="address-section">
        <h3>Địa chỉ nhận hàng</h3>
        <form className="address-form">
          <input
            type="text"
            placeholder="Họ và tên"
            name="name"
            value={address.name}
            onChange={(e) => setAddress({ ...address, name: e.target.value })}
            required
          />
          <input
            type="number"
            placeholder="Phone"
            name="phone"
            value={address.phone}
            onChange={(e) => setAddress({ ...address, phone: e.target.value })}
            required
          />
          <input
            type="text"
            placeholder="Tỉnh / Thành phố"
            name="province"
            value={address.province}
            onChange={(e) => setAddress({ ...address, province: e.target.value })}
            required
          />
          <input
            type="text"
            placeholder="Địa chỉ chi tiết"
            name="address"
            value={address.address}
            onChange={(e) => setAddress({ ...address, address: e.target.value })}
            required
          />
        </form>
      </div>

      <div className="payment-method-section">
        <h3>Phương thức thanh toán</h3>
        <label>
          <input
            type="radio"
            name="paymentMethod"
            value="cod"
            onChange={() => setPaymentMethod('cod')}
          />
          Thanh toán khi nhận hàng (COD)
        </label>
        <label>
          <input
            type="radio"
            name="paymentMethod"
            value="bank"
            onChange={() => setPaymentMethod('bank')}
          />
          Thanh toán qua ngân hàng
        </label>
        <label>
          <input
            type="radio"
            name="paymentMethod"
            value="credit"
            onChange={() => setPaymentMethod('credit')}
          />
          Thanh toán bằng thẻ tín dụng
        </label>
      </div>

      <div className="product-status">
        <h3>Trạng thái</h3>
        <span>Pending</span>
      </div>

      <div className="product-summary">
        <h3>Sản phẩm</h3>
        {selectedItems.map((item, index) => (
          <div className="summary-item" key={index}>
            <img src={item.product?.image[0]} alt={item.product?.name} />
            <div>
              <p>{item.product?.name}</p>
              <p>Số lượng: {item.quantity}</p>
              <p>Giá: đ{(item.product?.price * item.quantity).toLocaleString()}</p>
            </div>
          </div>
        ))}
      </div>

      <div className="payment-summary">
        <h3>Tổng thanh toán</h3>
        <p>đ{totalPrice.toLocaleString()}</p>
        <button className="btn-confirm" onClick={handlePlaceOrder}>
          Đặt hàng
        </button>
      </div>
    </div>
  );
};
