import React, { useState, useEffect } from 'react';
import { useAppContext } from '../../context/AppContext';
import './customerprofile.css';
import axios from 'axios';

export const CustomerProfile = () => {
    const { customer, customerProfile, setCustomerProfile } = useAppContext();
    const [formData, setFormData] = useState(customerProfile);
    const [loading, setLoading] = useState(false);


    useEffect(() => {
        setFormData(customerProfile);
    }, [customerProfile]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({
            ...prevData,
            [name]: value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);

        try {
            const token = localStorage.getItem('token');
            if (!token) {
                alert('You must be logged in to update your profile.');
                setLoading(false);
                return;
            }

            const response = await axios.put('http://localhost:3002/api/customer/profile', formData, {
                headers: {
                    Authorization: `Bearer ${token}`
                }
            });

            alert(response.data.message);
            setCustomerProfile(formData);

        } catch (error) {
            const errorMsg = error.response?.data?.message || 'An unexpected error occurred.';
            alert('Error: ' + errorMsg);
        } finally {
            setLoading(false);
        }
    };



    return (
        <div className="customer-profile">
            <h2>Thông Tin Khách Hàng</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Điện Thoại</label>
                    <input
                        type="text"
                        name="phone"
                        value={formData.phone}
                        onChange={handleChange}
                        required
                        className="input-phone"
                    />

                </div>

                <div className="form-group">
                    <label>Sinh Nhật</label>
                    <input
                        type="date"
                        name="birthday"
                        value={formData.birthday}
                        onChange={handleChange}
                        required
                        className="input-date"
                    />
                </div>

                <div className="form-group">
                    <label>Giới tính</label>
                    <select
                        name="gender"
                        value={formData.gender}
                        onChange={handleChange}
                        required
                        className="input-gender"
                    >
                        <option value="">Select Gender</option>
                        <option value="male">Male</option>
                        <option value="female">Female</option>
                        <option value="other">Other</option>
                    </select>
                </div>

                <div className="form-group">
                    <label>Thành phố</label>
                    <input
                        type="text"
                        name="city"
                        value={formData.city}
                        onChange={handleChange}
                        required
                        className="input-city"
                    />
                </div>

                <div className="form-group">
                    <label>Địa chỉ chi tiết</label>
                    <textarea
                        name="describe"
                        value={formData.describe}
                        onChange={handleChange}
                        required
                        className="input-describe"
                    />
                </div>

                <button type="submit" disabled={loading} className='updateProfile'>
                    {loading ? 'Updating...' : 'Update Profile'}
                </button>
            </form>


        </div>
    );
};
