import React, { useEffect, useState, useMemo } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import { Col } from 'antd';
import { useAppContext } from '../../context/AppContext';

export const ProductDetails = () => {
    const navigate = useNavigate();
    const { product, addToCart } = useAppContext();
    const { id } = useParams();

    const [thumbnail, setThumbnail] = useState(null);
    const [quantity, setQuantity] = useState(1);
    const [relatedProducts, setRelatedProducts] = useState([]);

    const currentProduct = useMemo(() => {
        return product.find((item) => item.id === Number(id));
    }, [product, id]);

    useEffect(() => {
        if (currentProduct) {
            setThumbnail(currentProduct?.img?.[0] || null);
            const related = product.filter(
                (item) => item.category === currentProduct.category && item.id !== currentProduct.id
            );
            setRelatedProducts(related.slice(0, 5));
        }
    }, [product, currentProduct]);

    const handleIncrease = () => {
        if (quantity < currentProduct.stock_quantity) {
            setQuantity(prev => prev + 1);
        }
    };

    const handleDecrease = () => {
        if (quantity > 1) {
            setQuantity(prev => prev - 1);
        }
    };

    const handleAddToCart = () => {
        addToCart(Number(currentProduct.id), quantity);
    };

    const handleBuyNow = () => {
        addToCart(Number(currentProduct.id), quantity);
        navigate("/cart");
    };

    if (!currentProduct) return <p>Đang tải sản phẩm...</p>;

    return (
        <div>
            <h2>{currentProduct.name}</h2>
            <img src={thumbnail} alt="main" width="200" />
            <div>
                {currentProduct.img.map((img, i) => (
                    <img
                        key={i}
                        src={img}
                        alt={`thumb-${i}`}
                        width="50"
                        onClick={() => setThumbnail(img)}
                        style={{ cursor: 'pointer', margin: 5 }}
                    />
                ))}
            </div>
            <p>Giá KM: {currentProduct.discounted_price.toLocaleString('vi-VN')}đ</p>
            <p>Giá gốc: {currentProduct.original_price.toLocaleString('vi-VN')}đ</p>
            <p>Tồn kho: {currentProduct.stock_quantity}</p>

            <div>
                <button onClick={handleDecrease}>-</button>
                <input type="text" value={quantity} readOnly />
                <button onClick={handleIncrease}>+</button>
            </div>

            <button onClick={handleAddToCart}>Thêm vào giỏ hàng</button>
            <button onClick={handleBuyNow}>Mua ngay</button>
        </div>
    );
};
