import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './userAdmin.css';

export const UserAdmin = () => {
  const [users, setUsers] = useState([]);
  const [error, setError] = useState('');

  const fetchUsers = async () => {
    try {
      const res = await axios.get('http://localhost:4005/api/auth/all');
      const sortedUsers = (res.data.users || []).sort((a, b) => {
        const roleOrder = { admin: 0, seller: 1, customer: 2 };
        return roleOrder[a.role] - roleOrder[b.role];
      });
      setUsers(sortedUsers);
    } catch (err) {
      console.error('Lỗi khi lấy dữ liệu:', err.message);
      setError('Không thể lấy danh sách người dùng.');
    }
  };

  return (
    <div className="user-admin-container">
      <h2>Danh sách người dùng</h2>
      {error && <p className="error-message">{error}</p>}
      <table className="user-table">
        <thead>
          <tr>
            <th>Tên</th>
            <th>Email</th>
            <th>Vai trò</th>
          </tr>
        </thead>
        <tbody>
          {users.length > 0 ? (
            users.map(user => (
              <tr key={user._id}>
                <td>{user.name}</td>
                <td>{user.email}</td>
                <td>{user.role}</td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="3">Không có người dùng nào.</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};
