import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './shoppages.css';

export const ShopPages = () => {
  const { sellerId } = useParams();
  const [products, setProducts] = useState([]);
  const [sellerInfo, setSellerInfo] = useState(null);

  useEffect(() => {
    const fetchShopData = async () => {
      try {
        const res = await axios.get(`http://localhost:4003/api/shop/${sellerId}`);
        setSellerInfo(res.data.seller);
        setProducts(res.data.products);
      } catch (error) {
        console.error('Lỗi khi lấy dữ liệu shop:', error);
      }
    };

    fetchShopData();
  }, [sellerId]);

  return (
    <div className="shop-page">
      {sellerInfo && (
        <div className="shop-header">
          <h2>{sellerInfo.storeName}</h2>
          <p>Địa chỉ: {sellerInfo.storeAddress}</p>
          <p>SĐT: {sellerInfo.phone}</p>
        </div>
      )}
      
      <h3>Danh sách sản phẩm</h3>
      <div className="product-list">
        {products.map((item) => (
          <div key={item._id} className="product-card">
            <img src={item.images[0]} alt={item.name} />
            <h4>{item.name}</h4>
            <p>{item.price} VND</p>
          </div>
        ))}
      </div>
    </div>
  );
};
