import React, { useState } from 'react';
import './addProducts.css';
import axios from 'axios';
import { useAppContext } from '../../context/AppContext';
import { useNavigate } from 'react-router-dom';

export const AddProducts = () => {
    const { categories, seller, user, token } = useAppContext();
    const navigate = useNavigate();

    const [productData, setProductData] = useState({
        name: '',
        description: '',
        category: '',
        status: '',
        price: 0,
        quantity: 0
    });

    const [images, setImages] = useState([]);

    const handleInputChange = (e) => {
        setProductData({ ...productData, [e.target.id]: e.target.value });
    };

    const handleImageChange = (e, index) => {
        const files = [...images];
        files[index] = e.target.files[0];
        setImages(files);
    };
    const handleSubmit = async (e) => {
        e.preventDefault();

        // Kiểm tra đăng nhập và quyền
        if (!seller || !token) {
            alert('Vui lòng đăng nhập trước khi thêm sản phẩm');
            return;
        }

        const userId = user._id;
        const sellerId = seller._id;

        // Kiểm tra hình ảnh đã được chọn
        if (images.length === 0) {
            alert('Vui lòng chọn ít nhất một hình ảnh');
            return;
        }

        // Log dữ liệu trước khi gửi
        console.log('Dữ liệu sản phẩm:', productData);
        console.log('Danh sách hình ảnh:', images);

        try {
            const formData = new FormData();
            formData.append('name', productData.name);
            formData.append('describe', productData.description);
            formData.append('categoryId', productData.category);
            formData.append('status', productData.status);
            formData.append('price', productData.price);
            formData.append('quantity', productData.quantity);
            formData.append('userId', userId);
            formData.append('sellerId', sellerId);

            // Thêm hình ảnh vào formData
            images.forEach((image) => {
                if (image) formData.append('image', image);
            });

            // Gửi yêu cầu POST
            const res = await axios.post('http://localhost:4003/api/product/create', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                    Authorization: `Bearer ${token}`
                }
            });

            alert('Sản phẩm đã được thêm thành công!');
            console.log(res.data);
        } catch (error) {
            console.error('Lỗi khi thêm sản phẩm:', error);
            if (error.response) {
                console.error('Lỗi từ backend:', error.response.data);
                alert(`Thêm sản phẩm thất bại. Lỗi: ${error.response.data.message || error.response.statusText}`);
            } else if (error.request) {
                console.error('Không nhận được phản hồi từ server:', error.request);
                alert('Lỗi kết nối đến server. Vui lòng thử lại sau.');
            } else {
                console.error('Lỗi khi gửi yêu cầu:', error.message);
                alert('Đã xảy ra lỗi khi thêm sản phẩm. Vui lòng thử lại.');
            }
        }
    };


    return (
        <div className="product-form">
            {/* <form className="form-container" onSubmit={handleSubmit}>
     
                <div className="form-group">
                    <label className="form-label">Hình ảnh sản phẩm</label>
                    <div className="image-upload-group">
                        {Array(1).fill('').map((_, index) => (
                            <label key={index} htmlFor={`image${index}`}>
                                <input
                                    accept="image/*"
                                    type="file"
                                    id={`image${index}`}
                                    name="images"
                                    hidden
                                    onChange={(e) => handleImageChange(e, index)}
                                />
                                <img
                                    className="upload-preview"
                                    src={
                                        images[index]
                                            ? URL.createObjectURL(images[index])
                                            : "https://raw.githubusercontent.com/prebuiltui/prebuiltui/main/assets/e-commerce/uploadArea.png"
                                    }
                                    alt="upload"
                                />
                            </label>
                        ))}
                    </div>
                </div>

              
                <div className="form-group">
                    <label htmlFor="name" className="form-label">Tên sản phẩm</label>
                    <input id="name" type="text" className="form-input" placeholder="Nhập vào đây" required onChange={handleInputChange} />
                </div>

    
                <div className="form-group">
                    <label htmlFor="description" className="form-label">Mô tả sản phẩm</label>
                    <textarea id="description" className="form-input" rows="4" placeholder="Nhập vào đây" onChange={handleInputChange}></textarea>
                </div>

       
                <div className="form-group">
                    <label htmlFor="category" className="form-label">Danh mục</label>
                    <select id="category" className="form-input" onChange={handleInputChange}>
                        <option value="">Chọn danh mục</option>
                        {categories.map(cat => (
                            <option key={cat._id} value={cat._id}>{cat.name}</option>
                        ))}
                    </select>

                    <label htmlFor="status" className="form-label">Trạng thái</label>
                    <select id="status" className="form-input" onChange={handleInputChange}>
                        <option value="in_stock">Còn hàng</option>
                        <option value="out_of_stock">Hết hàng</option>
                    </select>
                </div>

                <div className="form-row">
                    <div className="form-group small">
                        <label htmlFor="price" className="form-label">Giá sản phẩm</label>
                        <input id="price" type="number" className="form-input" placeholder="0" required onChange={handleInputChange} />
                    </div>
                    <div className="form-group small">
                        <label htmlFor="quantity" className="form-label">Số lượng</label>
                        <input id="quantity" type="number" className="form-input" placeholder="0" required onChange={handleInputChange} />
                    </div>
                </div>

                <button type="submit" className="submit-btn">ADD</button>

            </form> */}
        </div>
    );
};
