import React, { useEffect, useState } from 'react';
import axios from 'axios';

export const UserAdmin = () => {
  const [sellers, setSellers] = useState([]);
  const [customers, setCustomers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const sellerRes = await axios.get('http://localhost:3006/api/seller/all');
        const customerRes = await axios.get('http://localhost:3002/api/customer/all');

        setSellers(sellerRes.data.sellers || []);
        setCustomers(customerRes.data.customers || []);
        setLoading(false);
      } catch (err) {
        setError('Lỗi khi lấy dữ liệu');
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) return <div>Đang tải dữ liệu...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div>
      <h2>Thống kê tài khoản</h2>
      <p>Tổng Seller: {sellers.length}</p>
      <p>Tổng Customer: {customers.length}</p>

      <h3>Danh sách Seller</h3>
      <ul>
        {sellers.map((seller) => (
          <li key={seller._id}>
            {seller.userId?.name || 'Không có tên'} - Email: {seller.userId?.email || 'Không có email'}
          </li>
        ))}
      </ul>

      <h3>Danh sách Customer</h3>
      <ul>
        {customers.map((customer) => (
          <li key={customer._id}>
            {customer.userId?.name || 'Không có tên'} - Email: {customer.userId?.email || 'Không có email'}
          </li>
        ))}
      </ul>
    </div>
  );
};
