import React, { useState } from 'react';
import axios from 'axios';
import './customerForm.css';

const CustomerForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    birthday: '',
    gender: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:3002/api/customer/create', formData);
      alert('Lưu thông tin thành công!');
    } catch (error) {
      console.error('Lỗi khi lưu thông tin khách hàng:', error);
      alert('Lưu thất bại!');
    }
  };

  return (
    <div className="customer-form-overlay">
      <div className="customer-form">
        <h3>Thông tin khách hàng</h3>
        <form onSubmit={handleSubmit}>
          <input name="name" placeholder="Họ tên" onChange={handleChange} required />
          <input name="email" placeholder="Email" onChange={handleChange} required />
          <input name="phone" placeholder="Số điện thoại" onChange={handleChange} required />
          <input type="date" name="birthday" onChange={handleChange} />
          <select name="gender" onChange={handleChange}>
            <option value="">Chọn giới tính</option>
            <option value="Nam">Nam</option>
            <option value="Nữ">Nữ</option>
            <option value="Khác">Khác</option>
          </select>
          <div className="actions">
            <button type="submit">Lưu</button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default CustomerForm;
