import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './orderAdmin.css';
import { useAppContext } from '../../context/AppContext';
export const OrderAdmin = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const { token } = useAppContext();

  const fetchOrders = async () => {
    try {
      const response = await axios.get('http://localhost:4000/api/order/all', {
        headers: {
          Authorization: `Bearer ${token}`,
        }
      });
      setOrders(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Lỗi khi lấy danh sách đơn hàng:', error);
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchOrders();
  }, []);

  if (loading) return <p>Đang tải đơn hàng...</p>;

  return (
    <div className="order-admin">
      <h2>Danh sách tất cả đơn hàng</h2>
      <table>
        <thead>
          <tr>
            <th>Mã đơn</th>
            <th>Khách hàng</th>
            <th>Tổng tiền</th>
            <th>Trạng thái</th>
            <th>Phương thức thanh toán</th>
            <th>Vận chuyển</th>
            <th>Ngày tạo</th>
          </tr>
        </thead>
        <tbody>
          {orders.map(order => (
            <tr key={order._id}>
              <td>{order._id}</td>
              <td>{order.customerId?.fullName || 'Không có'}</td>
              <td>{JSON.stringify(order.customerId)}</td>
              <td>{order.totalOrder.toLocaleString()} VND</td>
              <td>{order.status}</td>
              <td>{order.paymentId?.paymentMethod || 'Không có'}</td>
              <td>{order.transportId?.shippingCarrier || 'Không có'}</td>
              <td>{new Date(order.createdAt).toLocaleString()}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};
