import React, { useEffect, useState } from 'react';
import './productdetails.css';
import { useParams, Link } from 'react-router-dom';
import { Button, Rate, Row, Col } from 'antd';
import { toSlug } from '../../utils/toSlug';
import { useAppContext } from '../../context/AppContext';

export const ProductDetails = () => {
  const { product , navigate} = useAppContext();  // Lấy dữ liệu từ context
  const { id } = useParams();  // Lấy ID sản phẩm từ URL
  const [relatedProducts, setRelatedProducts] = useState([]);  // Lưu các sản phẩm liên quan
  const [thumbnail, setThumbnail] = useState(null);  // Lưu thumbnail cho sản phẩm

  const currentProduct = product.find((item) => item.id === id);  

 

  // useEffect(() => {
  //   if (product.length > 0) {
  //     let productsCopy = product.slice();
  //     productsCopy = productsCopy.filter((item) => currentProduct.category === item.category);
  //     setRelatedProducts(productsCopy.slice(0, 5));
  //   }
  // }, [product, currentProduct]);

  // useEffect(() => {
  //   setThumbnail(currentProduct?.img?.[0] || null);
  // }, [currentProduct]);

  return (
    <div className="product-details">
      <nav>
        <p className="breadcrumb">
          <Link to="/">Trang chủ</Link> /
          <Link to={`/product/${toSlug(currentProduct.category)}`}>Sản phẩm</Link> /
          <span>{currentProduct.category}</span> /
          <span className="highlight">{currentProduct.name}</span>
        </p>
      </nav>

      <Row gutter={[16, 16]}>
        <Col span={8}>
          <div className="product-thumbnail">
            <img src={thumbnail} alt={currentProduct.name} />
          </div>
        </Col>
        <Col span={16}>
          <h1>{currentProduct.name}</h1>
          <p className="product-price">{currentProduct.price} VNĐ</p>
          <Rate defaultValue={currentProduct.rating} />
          <p>{currentProduct.description}</p>
          <Button type="primary">Mua ngay</Button>
        </Col>
      </Row>

      <div className="related-products">
        <h3>Sản phẩm liên quan</h3>
        <Row gutter={[16, 16]}>
          {relatedProducts.map((item) => (
            <Col span={6} key={item.id}>
              <Link to={`/product/${toSlug(item.category)}/${toSlug(item.name)}`}>
                <div className="related-product-card">
                  <img src={item.img[0]} alt={item.name} />
                  <h4>{item.name}</h4>
                  <p>{item.price} VNĐ</p>
                </div>
              </Link>
            </Col>
          ))}
        </Row>
      </div>
    </div>
  );
};
